    color(color_cord) {
        // Bottom edge cord
        translate([0, -width/2 - 1, thickness/2])
            cube([length + 4, 2, thickness + 2], center=true);

        // Top edge cord
        translate([0, width/2 + 1, thickness/2])
            cube([length + 4, 2, thickness + 2], center=true);

        // Side edge cords
        for(side = [-1, 1]) {
            translate([side * (length/2 + 1), 0, thickness/2])
                cube([2, width + 4, thickness + 2], center=true);
        }
