    color(color_cord) {
        // Front closure buttons
        for(y = [-width/3 : width/6 : width/3]) {
            translate([length/2 + 2, y, thickness - 2])
                sphere(r=2);
        }

        // Decorative buttons on shoulders
        for(side = [-1, 1]) {
            translate([side * length/3, width/2 - 5, thickness - 2])
                sphere(r=1.5);
        }
    }
}

// Lacing holes module
module lacing_holes() {
    color([0.3, 0.3, 0.3]) {
        // Front lacing holes
        for(i = [0:4]) {
            y = -width/6 + i * width/6;
            translate([length/2 + 1, y, thickness/2])
                rotate([0, 90, 0])
                cylinder(h=3, r=1.2, center=true);
        }
    }
}

// Assemble the aketon
