// Arming Doublet - Historical 15th century padded armor garment
// Designed for 3D printing in realistic style with medium complexity

scale_factor = 0.4;
$fn = 32;

module arming_doublet() {
    scale([scale_factor, scale_factor, scale_factor]) {
        // Main torso body - padded jack
        color([0.7, 0.5, 0.3]) {
            // Front torso
            difference() {
                union() {
                    // Main body - quilted appearance
                    for (i = [-2:2]) {
                        translate([i * 12, 0, 0]) {
                            cube([10, 45, 25], center=true);
                        }
                    }

                    // Upper chest reinforcement
                    translate([0, 10, 5]) {
                        cube([50, 20, 5], center=true);
                    }
                }

                // Arm holes
                translate([25, -5, 0]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=50, r=10, center=true);
                    }
                }
                translate([-25, -5, 0]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=50, r=10, center=true);
                    }
                }

                // Neck hole
                translate([0, 20, 5]) {
                    cylinder(h=30, r=8, center=true);
                }
            }

            // Back torso
            translate([0, -45, 0]) {
                difference() {
                    union() {
                        for (i = [-2:2]) {
                            translate([i * 12, 0, 0]) {
                                cube([10, 45, 25], center=true);
                            }
                        }
                    }

                    // Back arm holes
                    translate([25, 5, 0]) {
                        rotate([90, 0, 0]) {
                            cylinder(h=50, r=10, center=true);
                        }
                    }
                    translate([-25, 5, 0]) {
                        rotate([90, 0, 0]) {
                            cylinder(h=50, r=10, center=true);
                        }
                    }
                }
            }

            // Side panels connecting front and back
            translate([30, 0, 0]) {
                cube([5, 90, 25], center=true);
            }
            translate([-30, 0, 0]) {
                cube([5, 90, 25], center=true);
            }
        }

        // Arming points - leather laces for attaching armor
        color([0.4, 0.2, 0.1]) {
            // Shoulder points
            translate([20, 0, 15]) {
                cylinder(h=8, r=1.5);
            }
            translate([-20, 0, 15]) {
                cylinder(h=8, r=1.5);
            }

            // Side points for torso armor
            for (pos = [[15, -15], [15, 0], [15, 15], [-15, -15], [-15, 0], [-15, 15]]) {
                translate([pos[0], pos[1], 15]) {
                    cylinder(h=8, r=1.5);
                }
            }
        }

        // Decorative edging/binding
        color([0.6, 0.4, 0.2]) {
            // Front edging
            translate([0, 22.5, 12.5]) {
                cube([52, 2, 27]);
            }
            translate([0, -22.5, 12.5]) {
                cube([52, 2, 27]);
            }

            // Side edging
            translate([31, 0, 12.5]) {
                cube([2, 90, 27]);
            }
            translate([-31, 0, 12.5]) {
                cube([2, 90, 27]);
            }
        }

        // Quilted pattern details
        color([0.6, 0.4, 0.25]) {
            for (x = [-20:10:20]) {
                for (y = [-15:10:15]) {
                    // Front quilting stitches
                    translate([x, y, 13]) {
                        sphere(r=0.5);
                    }

                    // Back quilting stitches
                    translate([x, y - 45, 13]) {
                        sphere(r=0.5);
                    }
                }
            }
        }

        // Bottom skirt/faulds attachment points
        color([0.4, 0.2, 0.1]) {
            for (x = [-15:7.5:15]) {
                translate([x, -45, 10]) {
                    cylinder(h=5, r=1.5);
                }
            }
        }
    }
}

// Generate the complete arming doublet
arming_doublet();

// Cotehardie - 14th century fitted medieval garment
// Based on historical patterns with authentic proportions

// Overall dimensions (scaled for 3D printing)
garment_length = 70;
garment_width = 35;
sleeve_length = 25;
waist_height = 20;

// Main body module
module cotehardie_body() {
    // Torso - fitted upper body
    color("darkgreen")
    difference() {
        // Main torso block
        cube([garment_width, garment_length, 8], center=true);

        // Neck hole
        translate([0, garment_length/2 - 5, 0])
            cylinder(h=10, r=5, center=true);

        // Arm holes
        translate([garment_width/2 + 2, garment_length/2 - 8, 0])
            rotate([0, 90, 0])
                cylinder(h=15, r=6, center=true);

        translate([-garment_width/2 - 2, garment_length/2 - 8, 0])
            rotate([0, 90, 0])
                cylinder(h=15, r=6, center=true);
    }

    // Lower skirt - flared panels
    for (i = [-1, 1]) {
        translate([i * garment_width/4, -garment_length/2 + 10, 0])
            color("forestgreen")
            cube([garment_width/2 + 5, 20, 8], center=true);
    }
}

// Button closure module
module buttons() {
    for (i = [0:3:15]) {
        translate([garment_width/2 + 2, garment_length/2 - 10 - i, 0])
            color("gold")
            sphere(r=2);
    }
}

// Decorative heraldic embroidery
module heraldic_emblem() {
    // Chest embroidery - lion rampant
    translate([0, 10, 4.1])
    color("gold")
    union() {
        // Lion body
        cube([8, 12, 1]);
        // Lion head
        translate([-2, 12, 0])
            cube([4, 6, 1]);
        // Tail
        translate([6, 8, 0])
            rotate([0, 0, 45])
                cube([2, 8, 1]);
    }
}

// Sleeve module
module sleeve(side) {
    translate([side * (garment_width/2 + 6), garment_length/2 - 8, 0])
    rotate([0, 0, side * 15])
    color("darkgreen")
    union() {
        // Upper sleeve - fitted
        cube([10, sleeve_length, 8], center=true);

        // Lower sleeve - flared
        translate([0, -sleeve_length/2 - 8, 0])
            cube([15, 16, 8], center=true);

        // Button trim at cuff
        for (b = [-5, 0, 5]) {
            translate([b, -sleeve_length - 8, 4.1])
                color("gold")
                sphere(r=1.5);
        }
    }
}

// Belt module
module belt() {
    color("brown")
    translate([0, waist_height, 4.1])
    cube([garment_width + 10, 6, 2], center=true);

    // Belt buckle
    translate([garment_width/2 + 5, waist_height, 5.2])
    color("gold")
    union() {
        cube([4, 6, 1]);
        translate([2, 3, 0])
            cylinder(h=1, r=2);
    }
}

// Assembly
union() {
    cotehardie_body();
    sleeve(1);  // Right sleeve
    sleeve(-1); // Left sleeve
    buttons();
    heraldic_emblem();
    belt();
}

// Surcoat (tabard)  13th14th c. heraldic garment
// Realistic 3D-printable version, ~70 mm long

// Parameters (mm)
length = 70;
width  = 35;
thick  = 1.8;
hem    = 2.0;
armhole_r = 5;
neck_r    = 4;
cross_size = 12;

// Colors
tabard_col = "linen";
cross_col  = "red";

// Main tabard body
module tabard_body() {
    difference() {
        // Overall shape: rounded-bottom rectangle
        union() {
            translate([0, 0, thick/2])
                cube([width, length, thick], center=true);
            translate([0, length/2 - hem/2, thick/2])
                cylinder(r=width/2, h=thick, $fn=60);
        }

        // Armholes
        translate([ width/2 + armhole_r,  length/4, 0])
            rotate([90, 0, 0])
            cylinder(r=armhole_r, h=thick+1, $fn=20);
        translate([-width/2 - armhole_r,  length/4, 0])
            rotate([90, 0, 0])
            cylinder(r=armhole_r, h=thick+1, $fn=20);

        // Neck hole
        translate([0, -length/2 + neck_r*2, 0])
            rotate([90, 0, 0])
            cylinder(r=neck_r, h=thick+1, $fn=24);
    }
}

// Heraldic cross (St George)
module heraldic_cross() {
    color(cross_col)
    difference() {
        // Central cross arms
        union() {
            // Vertical bar
            translate([0, 0, thick])
                cube([cross_size/3.5, cross_size, thick]);
            // Horizontal bar
            translate([0, 0, thick])
                cube([cross_size, cross_size/3.5, thick]);
        }
        // Create cross outline by subtracting smaller cross
        translate([0, 0, thick-0.1])
        scale([0.65, 0.65, 1])
        union() {
            cube([cross_size/3.5, cross_size, thick+0.2]);
            cube([cross_size, cross_size/3.5, thick+0.2]);
        }
    }
}

// Belt with buckle
module belt() {
    color("brown")
    translate([0, 0, thick+0.5])
        cube([width+4, 4, 1.2], center=true);
    // Buckle
    color("silver")
    translate([width/2+1.5, 0, thick+1.1])
        difference() {
            cube([3, 5, 1]);
            translate([1, 2.5, -0.1])
                cube([1, 2, 1.3]);
        }
}

// Hem stitching pattern
module hem_stitch() {
    color("darkgray")
    for (a=[0:15:360])
        translate([width/2*sin(a), length/2 - hem, thick+0.2])
            sphere(d=0.8, $fn=8);
}

// Assembly
color(tabard_col) tabard_body();
heraldic_cross();
belt();
hem_stitch();

// Historical Medieval Tabard - OpenSCAD Model
// Based on 14th-15th century heraldic tabard design

// Overall dimensions for 3D printing
tabard_length = 80;
tabard_width = 50;
fabric_thickness = 2;

// Heraldic colors
heraldic_blue = [0.2, 0.3, 0.7];
heraldic_gold = [0.9, 0.7, 0.2];
heraldic_red = [0.7, 0.1, 0.1];
fabric_color = [0.8, 0.75, 0.65];

// Main tabard body
module tabard_body() {
    color(fabric_color)
    difference() {
        // Main rectangular body
        translate([0, -tabard_length/2, 0])
            cube([tabard_width, tabard_length, fabric_thickness], center=true);

        // Neck hole
        translate([0, tabard_length/4, -1])
            cylinder(h=fabric_thickness+2, r=8, $fn=32);

        // Side splits
        translate([tabard_width/2 - 3, -tabard_length/4, -1])
            cube([6, tabard_length/2, fabric_thickness+2]);
        translate([-tabard_width/2 + 3, -tabard_length/4, -1])
            cube([6, tabard_length/2, fabric_thickness+2]);
    }
}

// Heraldic shield emblem
module heraldic_shield() {
    color(heraldic_blue)
    translate([0, -10, fabric_thickness])
    linear_extrude(height=1)
    union() {
        // Shield shape
        difference() {
            // Main shield body
            translate([0, 0, 0])
                scale([1, 1.2, 1])
                    circle(r=12, $fn=5);

            // Bottom point
            translate([0, -15, 0])
                cube([24, 15, 24], center=true);
        }

        // Gold cross
        color(heraldic_gold)
        union() {
            // Vertical bar
            translate([0, 0, 1])
                cube([3, 20, 1]);
            // Horizontal bar
            translate([0, 0, 1])
                cube([16, 3, 1]);
        }

        // Four red lions (simplified)
        color(heraldic_red)
        for(x=[-6, 6])
            for(y=[-6, 6])
                translate([x, y, 2])
                    scale([0.3, 0.3, 1])
                        circle(r=4, $fn=3);
    }
}

// Decorative trim along edges
module edge_trim() {
    color(heraldic_gold)
    for(side=[-1, 1]) {
        // Side trims
        translate([side * tabard_width/2, 0, fabric_thickness/2])
            cube([1, tabard_length, fabric_thickness+1]);

        // Bottom trim pattern
        for(i=[-tabard_width/2+5:10:tabard_width/2-5]) {
            translate([i, -tabard_length/2+2, fabric_thickness])
                sphere(r=1.5, $fn=8);
        }

        // Top shoulder trim
        translate([side * tabard_width/4, tabard_length/2-5, fabric_thickness])
            rotate([0, 0, 45])
                cube([2, 15, fabric_thickness]);
    }
}

// Fabric folds texture
module fabric_folds() {
    color(fabric_color)
    difference() {
        union() {
            // Vertical folds
            for(i=[-tabard_width/2+8:8:tabard_width/2-8]) {
                translate([i, 0, fabric_thickness+0.5])
                    scale([0.5, tabard_length, 0.5])
                        cylinder(h=1, r=1, $fn=16);
            }

            // Horizontal folds at bottom
            for(i=[-tabard_length/2+10:10:tabard_length/2-20]) {
                translate([0, i, fabric_thickness+0.5])
                    scale([tabard_width, 0.5, 0.5])
                        cylinder(h=1, r=1, $fn=16);
            }
        }
    }
}

// Belt detail
module belt() {
    color([0.4, 0.2, 0.1])
    translate([0, -5, fabric_thickness+0.5])
    difference() {
        // Main belt
        cube([tabard_width+5, 4, 2], center=true);

        // Belt buckle hole
        translate([tabard_width/2-2, 0, -1])
            cylinder(h=4, r=1, $fn=16);
    }
}

// Main assembly
union() {
    tabard_body();
    heraldic_shield();
    edge_trim();
    fabric_folds();
    belt();
}

