// Historical Buckler Shield - OpenSCAD Model
// A 15th century style round buckler with decorative boss and riveted construction

// Overall dimensions
shield_diameter = 80;
shield_thickness = 4;
boss_diameter = 35;
boss_height = 12;
handle_length = 70;
handle_diameter = 8;

// Wall thickness for 3D printing
wall_thickness = 2;

// Main shield body
module shield_body() {
    color("burlywood") difference() {
        // Main circular shield
        cylinder(h = shield_thickness, d = shield_diameter, $fn = 64);

        // Central hole for boss
        translate([0, 0, -1]) 
            cylinder(h = shield_thickness + 2, d = boss_diameter - 10, $fn = 32);
    }
}

// Decorative central boss (umbos)
module central_boss() {
    color("darkgoldenrod") union() {
        // Main dome
        translate([0, 0, shield_thickness]) 
            scale([1, 1, 0.8]) 
                sphere(d = boss_diameter, $fn = 32);

        // Boss base ring
        translate([0, 0, shield_thickness]) 
            cylinder(h = 2, d = boss_diameter + 4, $fn = 32);

        // Decorative rivets around boss
        for(i = [0:7]) {
            rotate([0, 0, i * 45]) 
                translate([(boss_diameter + 8)/2, 0, shield_thickness + 1]) 
                    cylinder(h = 2, d = 3, $fn = 8);
        }
    }
}

// Reinforcement bands on back
module reinforcement_bands() {
    color("saddlebrown") union() {
        // Horizontal band
        translate([0, 0, shield_thickness/2 - 1]) 
            cube([shield_diameter - 10, 6, 2], center = true);

        // Vertical band
        translate([0, 0, shield_thickness/2 - 1]) 
            cube([6, shield_diameter - 10, 2], center = true);
    }
}

// Handle grip
module handle() {
    color("saddlebrown") union() {
        // Main handle bar
        translate([0, -handle_length/2, -handle_diameter/2 - 2]) 
            rotate([90, 0, 0]) 
                cylinder(h = handle_length, d = handle_diameter, $fn = 16);

        // Handle supports
        for(pos = [-20, 20]) {
            translate([pos, 0, -handle_diameter/2 - 2]) 
                cube([4, handle_diameter, handle_diameter], center = true);
        }
    }
}

// Decorative edge reinforcement with rivets
module edge_reinforcement() {
    color("darkgoldenrod") union() {
        // Metal edge band
        difference() {
            translate([0, 0, shield_thickness/2]) 
                cylinder(h = shield_thickness + 2, d = shield_diameter + 4, $fn = 64);

            translate([0, 0, -1]) 
                cylinder(h = shield_thickness + 4, d = shield_diameter, $fn = 64);
        }

        // Rivets around edge
        for(i = [0:11]) {
            rotate([0, 0, i * 30]) 
                translate([(shield_diameter + 2)/2, 0, shield_thickness/2]) 
                    cylinder(h = shield_thickness + 2, d = 2.5, $fn = 6);
        }
    }
}

// Assembly
union() {
    // Main components
    shield_body();
    central_boss();
    edge_reinforcement();

    // Back side components (mirrored for display)
    mirror([0, 0, 1]) {
        translate([0, 0, shield_thickness + boss_height + 5]) {
            reinforcement_bands();
            handle();
        }
    }
}

// Roman Hypocaust Tile Heater - Historical Artifact
// A terracotta box used in Roman bathhouse under-floor heating systems
// Features decorative patterns and air flow holes for hot air circulation

$fn = 32;

// Overall dimensions (mm)
tile_width = 80;
tile_length = 80;
tile_height = 25;
wall_thickness = 3;

// Colors
terracotta = [0.8, 0.4, 0.2];
darker_terracotta = [0.7, 0.35, 0.15];

// Main tile body
module tile_body() {
    difference() {
        // Outer shell
        color(terracotta) cube([tile_width, tile_length, tile_height], center = true);

        // Hollow interior
        translate([0, 0, -1])
            cube([tile_width - 2*wall_thickness, tile_length - 2*wall_thickness, tile_height], center = true);
    }
}

// Decorative border pattern
module decorative_border() {
    for (i = [-1, 1]) {
        for (j = [-1, 1]) {
            translate([i * (tile_width/2 - 5), j * (tile_length/2 - 5), tile_height/2])
                color(darker_terracotta) cylinder(r = 4, h = 1.5);
        }
    }
}

// Air flow holes (characteristic of hypocaust tiles)
module air_holes() {
    // Central large hole
    translate([0, 0, tile_height/2 - 1])
        color(darker_terracotta) cylinder(r = 8, h = 2);

    // Smaller corner holes
    for (x = [-tile_width/3, tile_width/3]) {
        for (y = [-tile_length/3, tile_length/3]) {
            translate([x, y, tile_height/2 - 1])
                color(darker_terracotta) cylinder(r = 5, h = 2);
        }
    }
}

// Roman numeral markings (historical authenticity)
module roman_numerals() {
    translate([0, 0, tile_height/2 + 0.5]) {
        color(darker_terracotta) {
            // "VII" for VIIth Legion
            translate([-10, 0, 0]) cube([2, 10, 0.5]);
            translate([-5, 0, 0]) cube([2, 10, 0.5]);
            translate([5, 0, 0]) cube([2, 10, 0.5]);
            translate([10, 0, 0]) cube([2, 10, 0.5]);
            translate([15, 0, 0]) cube([2, 10, 0.5]);
            translate([20, 0, 0]) cube([2, 10, 0.5]);
            translate([10, -5, 0]) cube([20, 2, 0.5]);
        }
    }
}

// Support feet for stacking
module support_feet() {
    for (x = [-tile_width/3, tile_width/3]) {
        for (y = [-tile_length/3, tile_length/3]) {
            translate([x, y, -tile_height/2 + 2])
                color(terracotta) cylinder(r = 6, h = 4);
        }
    }
}

// Worn edges for realistic aging
module worn_edges() {
    translate([tile_width/2 - 2, 0, 0])
        rotate([0, 0, 45])
        color(terracotta) cube([3, 15, tile_height], center = true);
    translate([-tile_width/2 + 2, 0, 0])
        rotate([0, 0, -45])
        color(terracotta) cube([3, 15, tile_height], center = true);
}

// Assembly
union() {
    tile_body();
    decorative_border();
    air_holes();
    roman_numerals();
    support_feet();
    worn_edges();
