// Ancient Greek Black-Figure Kylix Bowl (ca. 530 BCE)
// A drinking cup with characteristic wide shallow bowl and raised handles
// Designed for 3D printing with 2mm wall thickness and no overhangs

$fn = 100;

// Overall dimensions
bowl_diameter = 80;
bowl_height = 25;
foot_diameter = 35;
foot_height = 8;
handle_width = 8;
handle_height = 12;

// Wall thickness for 3D printing
wall_thickness = 2;

// Main bowl body
module bowl_body() {
    difference() {
        // Outer bowl shape
        cylinder(h = bowl_height, d1 = bowl_diameter, d2 = bowl_diameter * 0.85);

        // Inner hollow space
        translate([0, 0, wall_thickness])
        cylinder(h = bowl_height - wall_thickness, d1 = bowl_diameter - 2 * wall_thickness, d2 = bowl_diameter * 0.85 - 2 * wall_thickness);
    }
}

// Pedestal foot
module foot() {
    union() {
        // Main foot
        cylinder(h = foot_height, d1 = foot_diameter * 0.7, d2 = foot_diameter);

        // Hollow inside foot
        difference() {
            cylinder(h = foot_height, d1 = foot_diameter * 0.7, d2 = foot_diameter);
            translate([0, 0, wall_thickness])
            cylinder(h = foot_height - wall_thickness, d1 = foot_diameter * 0.7 - 2 * wall_thickness, d2 = foot_diameter - 2 * wall_thickness);
        }
    }
}

// Characteristic Greek handles (2 horizontal loops)
module handle() {
    difference() {
        // Outer handle loop
        rotate([90, 0, 0])
        torus(major = handle_height/2, minor = handle_width/2);

        // Inner hole
        rotate([90, 0, 0])
        torus(major = handle_height/2 - wall_thickness, minor = handle_width/2 - wall_thickness);
    }
}

// Decorative black-figure pattern band
module decorative_band() {
    color("darkslategray")
    translate([0, 0, bowl_height - 8])
    difference() {
        cylinder(h = 6, d = bowl_diameter * 0.9);
        cylinder(h = 6, d = bowl_diameter * 0.9 - 2 * wall_thickness);
    }
}

// Painted meander pattern (Greek key motif)
module meander_pattern() {
    for(i = [0:15]) {
        rotate([0, 0, i * 22.5])
        translate([bowl_diameter * 0.42, 0, bowl_height - 5])
        color("black")
        cube([4, 2, 2]);
    }
}

// Central tondo decoration (mythological scene)
module tondo_deco() {
    translate([0, 0, bowl_height - 10])
    union() {
        // Central circle
        color("black")
        cylinder(h = 1, d = 20);

        // Decorative rays
        for(i = [0:11]) {
            rotate([0, 0, i * 30])
            translate([8, 0, 0.5])
            color("black")
            cube([6, 1, 1]);
        }
    }
}

// Assemble the complete kylix
union() {
    // Main bowl
    color("saddlebrown")
    bowl_body();

    // Foot pedestal
    color("saddlebrown")
    translate([0, 0, -foot_height])
    foot();

    // Attach handles on sides
    color("saddlebrown")
    translate([bowl_diameter/2 - handle_width/2, 0, bowl_height/2])
    handle();

    color("saddlebrown")
    translate([-bowl_diameter/2 + handle_width/2, 0, bowl_height/2])
    mirror([1, 0, 0])
    handle();

    // Decorative elements
    decorative_band();
    meander_pattern();
    tondo_deco();
}

// Ensure flat bottom for printing
translate([-bowl_diameter/2 - 5, -bowl_diameter/2 - 5, -foot_height - 1])
color("lightgray", 0.2)
cube([bowl_diameter + 10, bowl_diameter + 10, 1]);

// Ancient Greek Kylix (drinking cup) - 5th century BCE style
// Dimensions: 90mm diameter, 45mm height

// Wall thickness for 3D printing
wall = 1.5;

// Main cup body module
module kylix_body() {
    color("cornsilk") difference() {
        // Outer bowl shape
        cylinder(h=25, r1=45, r2=40, $fn=100);

        // Inner cavity
        translate([0, 0, -1])
            cylinder(h=27, r1=43.5-wall, r2=38.5-wall, $fn=100);
    }
}

// Foot base module
module kylix_foot() {
    color("cornsilk") union() {
        // Main foot
        cylinder(h=8, r1=35, r2=30, $fn=100);

        // Stem
        translate([0, 0, 8])
            cylinder(h=12, r1=8, r2=12, $fn=50);
    }
}

// Handle module - creates one handle
module handle(side) {
    color("cornsilk") {
        // Handle attachment points
        translate([side * 40, 0, 15])
            sphere(r=4, $fn=20);

        // Curved handle
        translate([side * 50, 0, 15])
            rotate([90, 0, side * 90])
            difference() {
                // Outer handle
                rotate_extrude(angle=90, $fn=50)
                    translate([10, 0, 0])
                    circle(r=3, $fn=20);

                // Inner cavity
                rotate_extrude(angle=90, $fn=50)
                    translate([10, 0, 0])
                    circle(r=1.5, $fn=20);
            }

        // Handle attachment points
        translate([side * 40, 0, 15])
            sphere(r=4, $fn=20);
    }
}

// Decorative meander pattern on rim
module meander_pattern() {
    color("saddlebrown") {
        for(i = [0:15:360]) {
            rotate([0, 0, i])
                translate([44.5, 0, 24])
                    cube([2, 3, 1], center=true);

            rotate([0, 0, i+7.5])
                translate([44.5, 0, 24])
                    cube([3, 2, 1], center=true);
        }
    }
}

// Central tondo decoration (typical black-figure scene)
module tondo_decoration() {
    color("saddlebrown") {
        // Central medallion
        translate([0, 0, 0.5])
            cylinder(h=0.5, r=20, $fn=100);

        // Decorative border
        for(i = [0:30:360]) {
            rotate([0, 0, i])
                translate([18, 0, 1])
                    cube([2, 1, 1], center=true);
        }

        // Stylized figure (simplified hoplite warrior)
        translate([0, 0, 1]) {
            // Body
            cylinder(h=0.5, r=8, $fn=6);

            // Shield
            translate([-8, 0, 0])
                rotate([90, 0, 0])
                cylinder(h=0.5, r=4, $fn=20);

            // Spear
            translate([5, 0, 0])
                rotate([90, 0, 45])
                cube([12, 0.5, 0.5], center=true);
        }
    }
}

// Main assembly
union() {
    // Assemble the cup
    translate([0, 0, 20])
        kylix_body();

    // Add foot
    kylix_foot();

    // Add handles
    handle(1);   // Right handle
    handle(-1);  // Left handle

    // Add decorations
    meander_pattern();
    tondo_decoration();
