// Main plate body
module plate_body() {
    // Base ring for stability
    difference() {
        cylinder(h=base_ring_height, d1=plate_diameter-20, d2=plate_diameter-15, $fn=64);
        cylinder(h=base_ring_height+1, d1=plate_diameter-22, d2=plate_diameter-17, $fn=64);
    }

    // Main bowl shape
    translate([0, 0, base_ring_height]) {
        difference() {
            cylinder(h=plate_height, d1=plate_diameter-15, d2=plate_diameter, $fn=128);
            // Hollow interior
            translate([0, 0, wall_thickness]) {
                cylinder(h=plate_height-wall_thickness+1, d1=plate_diameter-15-wall_thickness*2, d2=plate_diameter-wall_thickness*2, $fn=128);
            }
        }
    }
