// Decorative meander pattern border
module meander_pattern() {
    for(i=[0:11]) {
        rotate([0, 0, i*30]) {
            translate([plate_diameter/2-8, 0, plate_height-1]) {
                cube([6, 2, 1]);
            }
            translate([plate_diameter/2-8, 3, plate_height-1]) {
                cube([2, 6, 1]);
            }
        }
    }
}

// Greek key pattern on rim
