    for(i=[0:23]) {
        rotate([0, 0, i*15]) {
            translate([plate_diameter/2-3, 0, plate_height-0.5]) {
                cube([1, 3, 0.5]);
            }
        }
    }
}

// Black figure scene - warrior and horse
module black_figure_scene() {
    // Warrior figure (simplified silhouette)
    translate([0, -10, plate_height-2]) {
        scale([0.1, 0.1, 0.2]) {
            // Body
            cube([20, 15, 5]);
            // Shield
            translate([3, 8, 0]) {
                cylinder(h=6, d=10);
            }
            // Spear
            translate([15, -5, 0]) {
                rotate([0, 0, 45]) {
                    cube([2, 30, 2]);
                }
            }
        }
    }

    // Horse figure
    translate([15, 5, plate_height-2]) {
        scale([0.08, 0.08, 0.15]) {
            // Body
            cube([40, 20, 8]);
            // Head
            translate([35, 5, 0]) {
                cube([15, 8, 6]);
            }
            // Legs
            for(x=[-5, 15]) {
                for(y=[-8, 8]) {
                    translate([x, y, -10]) {
                        cube([3, 3, 10]);
