                    translate([8, 0, 0]) {
                        cube([2, 1, 1]);
                    }
                }
            }
        }
    }
}

// Assemble the complete plate
color(ceramic_color) {
    plate_body();

    // Apply orange-red clay color to interior
    color(figure_color) {
        intersection() {
            plate_body();
            translate([0, 0, base_ring_height+wall_thickness]) {
                cylinder(h=plate_height-wall_thickness, d=plate_diameter-wall_thickness*2-2, $fn=128);
            }
        }
    }

    // Add decorative elements
    color(ceramic_color) {
        meander_pattern();
        greek_key();
        black_figure_scene();
        central_boss();
    }
}

