// Iron Age Celtic Torc-style Ring
// Based on archaeological finds from La Tne culture (450-1 BCE)
// Features twisted rope pattern and terminal knobs

$fn = 64;  // Smooth curves for 3D printing

// Main ring parameters
ring_outer_diameter = 60;
ring_inner_diameter = 45;
ring_height = 12;
wall_thickness = 3;

// Terminal knobs
knob_diameter = 14;
knob_height = 10;

// Rope pattern parameters
rope_twists = 8;
rope_depth = 1.5;

// Colors
color_iron = [0.4, 0.35, 0.3];
color_dark_iron = [0.3, 0.25, 0.2];

module twisted_rope_pattern() {
    // Create twisted rope effect around ring
    for (i = [0:rope_twists-1]) {
        rotate([0, 0, i * (360/rope_twists)]) {
            translate([0, 0, -ring_height/2]) {
                // Twisted strands
                rotate([0, 0, 45]) {
                    linear_extrude(height=ring_height, twist=20) {
                        difference() {
                            circle(d=ring_outer_diameter - wall_thickness);
                            circle(d=ring_inner_diameter + wall_thickness);
                        }
                    }
                }
            }
        }
    }
}

module decorative_grooves() {
    // Add parallel grooves for La Tne style decoration
    for (i = [1:3]) {
        rotate([0, 90, 0]) {
            translate([0, 0, -ring_height/2 + i * (ring_height/4)]) {
                difference() {
                    cylinder(h=ring_outer_diameter, d=ring_outer_diameter - wall_thickness/2, center=true);
                    cylinder(h=ring_outer_diameter + 2, d=ring_outer_diameter - wall_thickness/2 - rope_depth, center=true);
                }
            }
        }
    }
}

module terminal_knob(position) {
    // Celtic-style terminal knobs
    translate(position) {
        color(color_dark_iron) {
            // Main knob body
            sphere(d=knob_diameter);

            // Decorative ridges
            translate([0, 0, -knob_height/4]) {
                cylinder(h=knob_height/2, d=knob_diameter * 0.8);
            }

            // Knob top detail
            translate([0, 0, knob_height/3]) {
                sphere(d=knob_diameter * 0.6);
            }
        }
    }
}

module ring_body() {
    // Create the main torc ring
    rotate_extrude(angle=350) {  // Small gap for 3D printing support
        translate([(ring_outer_diameter + ring_inner_diameter)/4, 0, 0]) {
            difference() {
                // Outer profile
                square([ring_outer_diameter/2 - ring_inner_diameter/2, ring_height], center=true);

                // Inner hollow
                translate([0, 0, ring_height/2 - wall_thickness]) {
                    cube([ring_outer_diameter/2 - ring_inner_diameter/2 - wall_thickness*2, ring_height - wall_thickness*2, wall_thickness*2], center=true);
                }
            }
        }
    }
}

// Main assembly
color(color_iron) {
    difference() {
        union() {
            // Ring body with twisted rope texture
            ring_body();

            // Add rope pattern details
            twisted_rope_pattern();

            // Terminal knobs at ends
            terminal_knob([0, (ring_outer_diameter + ring_inner_diameter)/4, 0]);
            terminal_knob([0, -(ring_outer_diameter + ring_inner_diameter)/4, 0]);
        }

        // Remove bottom face for 3D printing support
        translate([0, 0, -ring_height/2 - 1]) {
            cube([ring_outer_diameter + 10, ring_outer_diameter + 10, 2], center=true);
        }
    }
}

