// Viking Oval Brooch (Pair Style) - 9th-10th Century Historical Artifact
// Based on archaeological finds from Birka, Sweden
// Overall dimensions: 70mm x 45mm x 12mm

// Main body parameters
body_length = 70;
body_width = 45;
body_thickness = 8;

// Pin and hinge parameters
pin_length = 40;
pin_diameter = 2.5;
hinge_diameter = 8;
hinge_thickness = 3;

// Decorative elements
boss_radius = 6;
boss_height = 3;
nubs = 12;
nub_radius = 1.5;
nub_height = 1;

// Geometric pattern parameters
line_width = 1;
line_depth = 0.5;
circle_radius = 3;

// Color definitions
bronze_color = [0.72, 0.45, 0.2];
patina_color = [0.2, 0.4, 0.2];

// Main brooch module
module viking_brooch() {
    // Main oval body with slight dome
    color(bronze_color) {
        difference() {
            // Base oval body
            scale([1, 0.65, 1])
                cylinder(h=body_thickness, r=body_length/2, $fn=64);

            // Hollow center
            translate([0, 0, -0.1])
                scale([1, 0.65, 1])
                    cylinder(h=body_thickness+0.2, r=body_length/2-6, $fn=64);
        }

        // Upper dome
        translate([0, 0, body_thickness])
            scale([1, 0.65, 0.3])
                cylinder(h=4, r=body_length/2, $fn=64);
    }

    // Central boss
    color(bronze_color) {
        translate([0, 0, body_thickness+4])
            cylinder(h=boss_height, r=boss_radius, $fn=32);
    }

    // Decorative nubs around edge
    color(bronze_color) {
        for(i = [0:nubs-1]) {
            angle = i * 360 / nubs;
            x = cos(angle) * (body_length/2 - 8);
            y = sin(angle) * (body_length/2 - 8) * 0.65;

            translate([x, y, 0])
                cylinder(h=nub_height+body_thickness, r=nub_radius, $fn=16);
        }
    }

    // Geometric incised decoration
    color(patina_color) {
        // Triangular patterns
        for(i = [0:5]) {
            angle = i * 60;
            x1 = cos(angle) * 15;
            y1 = sin(angle) * 15 * 0.65;
            x2 = cos(angle+30) * 25;
            y2 = sin(angle+30) * 25 * 0.65;

            translate([0, 0, body_thickness-0.1])
                linear_extrude(height=line_depth+0.1)
                    polygon(points=[[0,0], [x1,y1], [x2,y2]]);
        }

        // Concentric circles
        for(r = [10:5:25]) {
            translate([0, 0, body_thickness-0.1])
                scale([1, 0.65, 1])
                    cylinder(h=line_depth+0.1, r1=r, r2=r-line_width, $fn=64);
        }
    }

    // Pin hinge mechanism
    color(bronze_color) {
        // Hinge tubes
        translate([-body_length/2 + 8, 0, 0])
            rotate([90, 0, 0])
                cylinder(h=hinge_thickness, r=hinge_diameter/2, $fn=16);

        translate([-body_length/2 + 12, 0, 0])
            rotate([90, 0, 0])
                cylinder(h=hinge_thickness, r=hinge_diameter/2, $fn=16);

        // Pin
        translate([-body_length/2 + 10, 0, -hinge_thickness/2])
            rotate([90, 0, 0])
                cylinder(h=pin_length, r=pin_diameter/2, $fn=8);
    }

    // Attachment loops on back
    color(bronze_color) {
        translate([body_length/2 - 15, 0, -body_thickness])
            difference() {
                cube([8, 15, 3], center=true);
                translate([0, 0, -0.1])
                    cube([10, 11, 3.2], center=true);
            }

        translate([-body_length/2 + 15, 0, -body_thickness])
            difference() {
                cube([8, 15, 3], center=true);
                translate([0, 0, -0.1])
                    cube([10, 11, 3.2], center=true);
            }
    }
}

