    difference() {
        // Outer tube
        rotate_extrude(angle = 300) {
            translate([bangle_diameter/2, 0, 0])
            circle(r = wire_thickness);
        }
        // Inner hollow
        rotate_extrude(angle = 300) {
            translate([bangle_diameter/2, 0, 0])
            circle(r = wire_thickness - wall_thickness);
        }
