module spiral_decoration() {
    // Celtic spiral motifs along the bangle
    for (i = [0:15:300]) {
        rotate([0, 0, i])
        translate([bangle_diameter/2 + 2, 0, 0])
        rotate([90, 0, 0])
        scale([1, 1, 0.3])
        color(bronze_light) {
            difference() {
                cylinder(h = 1.5, r1 = 3, r2 = 2);
                for (j = [0:90:270]) {
                    rotate([0, 0, j])
                    translate([1.2, 0, -0.5])
                    cylinder(h = 2.5, r = 0.8);
                }
            }
        }
    }
}

module terminals() {
    // Traditional Celtic zoomorphic terminals
    // Left terminal
    rotate([0, 0, 0])
    translate([bangle_diameter/2 - 3, 0, 0])
    color(bronze_dark) {
        // Terminal base
        rotate([0, 90, 0])
        cylinder(h = 8, r1 = 7, r2 = 5);

        // Beak detail
        translate([0, 4, 0])
        rotate([0, 90, 0])
        scale([1, 1.5, 1])
        cylinder(h = 4, r1 = 2.5, r2 = 0);

        // Eye socket
        translate([0, 2, 2.5])
        rotate([0, 90, 0])
        sphere(r = 1.2);
    }

    // Right terminal (mirrored)
    rotate([0, 0, 300])
    translate([bangle_diameter/2 - 3, 0, 0])
    scale([-1, 1, 1])
    color(bronze_dark) {
        rotate([0, 90, 0])
        cylinder(h = 8, r1 = 7, r2 = 5);

        translate([0, 4, 0])
        rotate([0, 90, 0])
        scale([1, 1.5, 1])
        cylinder(h = 4, r1 = 2.5, r2 = 0);

        translate([0, 2, 2.5])
        rotate([0, 90, 0])
        sphere(r = 1.2);
