gold = "#FFD700";

// Main torc body - split C-shape with gap
module torc_body() {
    difference() {
        // Full torus
        rotate([90, 0, 0])
        torus(torc_radius, torc_thickness);

        // Gap for opening
        translate([0, -torc_radius - 5, -torc_thickness - 2])
        cube([gap_width, torc_radius * 2 + 10, torc_thickness * 2 + 4]);
    }
}

// Helper for torus
module torus(r, thickness) {
    rotate_extrude()
    translate([r, 0, 0])
    circle(thickness);
}

// Decorative terminal knobs
module terminal_knob() {
    color(gold)
    difference() {
        // Main knob sphere
        sphere(torc_thickness * 1.2);

        // Recessed pattern
        for(i = [0:4]) {
            rotate([0, 0, i * 72])
            translate([torc_thickness * 0.8, 0, 0])
            cylinder(h = torc_thickness * 2.5, r = 0.5, $fn = 6);
        }

        // Central boss
        translate([0, 0, torc_thickness * 1.1])
        cylinder(h = torc_thickness * 0.3, r = torc_thickness * 0.4, $fn = 32);
    }
}

// Twisted rope pattern
module twisted_pattern() {
    color(bronze)
    for(i = [0:360/num_beads:360-360/num_beads]) {
        rotate([0, 0, i])
        translate([torc_radius, 0, 0])
        rotate([90, 0, 0])
        cylinder(h = torc_thickness * 2.2, r = 0.8, $fn = 8);
    }
}

// Bead decorations
module decorative_beads() {
    color(gold)
    for(i = [0:360/num_beads:360-360/num_beads]) {
        rotate([0, 0, i + 360/(num_beads*2)])
        translate([torc_radius, 0, 0])
        sphere(bead_diameter/2);
    }
}

// Engraved spiral pattern
module spiral_pattern() {
    color(bronze)
    for(angle = [0:15:345]) {
        rotate([0, 0, angle])
        translate([torc_radius + torc_thickness * 0.3, 0, 0])
        rotate([90, 0, 0])
        cylinder(h = 0.5, r = 0.2, $fn = 8);
    }
}

// Main assembly
