    // Main body
    color(bronze)
    torc_body();

    // Twisted pattern on surface
    twisted_pattern();

    // Decorative beads
    decorative_beads();

    // Spiral engraving
    spiral_pattern();

    // Terminal knobs at ends
    translate([gap_width/2, -torc_radius - 2, 0])
    terminal_knob();

    translate([-gap_width/2, -torc_radius - 2, 0])
    mirror([1, 0, 0])
    terminal_knob();
