thickness = 4;          // Band thickness
width = 8;              // Band width
segments = 64;          // Circle resolution

// Decorative elements
num_spikes = 12;        // Number of sun ray spikes
spike_height = 15;      // Height of decorative spikes
spike_width = 3;        // Width at base of spike

// Engraving details
engraving_depth = 0.5;  // Depth of engraved patterns
num_engravings = 24;    // Number of engraved lines

// Colors
bronze_color = [0.72, 0.45, 0.2];  // Bronze color
patina_color = [0.2, 0.4, 0.3];    // Green patina

// Main circlet band
module circlet_band() {
    color(bronze_color) {
        difference() {
            // Main torus shape for the band
            rotate_extrude($fn = segments)
                translate([diameter/2, 0, 0])
                    square([thickness, width], center=true);

            // Inner hollow for wearing
            rotate_extrude($fn = segments)
                translate([diameter/2 - thickness, 0, 0])
                    square([thickness - 1, width - 1], center=true);
        }
    }
}

// Decorative sun ray spikes
module sun_spikes() {
    for(i = [0:num_spikes-1]) {
        angle = i * 360 / num_spikes;
        rotate([0, 0, angle])
            translate([diameter/2 + thickness/2, 0, 0])
                rotate([90, 0, 0])
                    color(bronze_color) {
                        // Spike base
                        cylinder(h=spike_height/3, r1=spike_width, r2=spike_width*0.8, $fn=8);
                        // Spike tip
                        translate([0, 0, spike_height/3])
                            cylinder(h=spike_height*2/3, r1=spike_width*0.8, r2=0.5, $fn=8);
                    }
    }
}

// Engraved patterns
module engravings() {
    for(i = [0:num_engravings-1]) {
        angle = i * 360 / num_engravings;
        rotate([0, 0, angle])
            translate([diameter/2 + thickness/2, 0, width/2 - engraving_depth])
                color(patina_color) {
                    cube([thickness + 1, 1, engraving_depth], center=true);
                }
    }
}

// Central decorative boss (sun symbol)
module central_boss() {
    color(bronze_color) {
        // Main boss
        translate([diameter/2, 0, width/2 + 2])
            rotate([0, 90, 0])
                cylinder(h=6, r=5, $fn=32);

        // Sun wheel spokes
        for(i = [0:7]) {
            angle = i * 45;
            rotate([0, 0, angle])
                translate([diameter/2, 0, width/2 + 2])
                    rotate([0, 90, 0])
                        cube([8, 1, 1], center=true);
        }
    }
}

// Patina effect spots
module patina_spots() {
    random_seed = 42;
    for(i = [0:20]) {
        angle = rands(0, 360, 1, random_seed + i)[0];
        height = rands(-width/2, width/2, 1, random_seed + i)[0];
        rotate([0, 0, angle])
            translate([diameter/2 + thickness/2, 0, height])
                color(patina_color) {
                    sphere(r=0.5, $fn=8);
                }
    }
}

// Assembly
union() {
    circlet_band();
    sun_spikes();
    engravings();
    central_boss();
    patina_spots();
}

