module collar_base() {
    // Create the curved collar shape
    difference() {
        // Main collar body
        union() {
            color(faience_blue)
            linear_extrude(height = thickness)
            polygon(points = [
                [-collar_width/2, 0],
                [-collar_width/2 + 8, collar_height],
                [collar_width/2 - 8, collar_height],
                [collar_width/2, 0],
                [collar_width/2 - 15, -8],
                [-collar_width/2 + 15, -8]
            ]);
        }

        // Cut center hole for neck
        translate([0, 5, -1])
        cylinder(h = thickness + 2, r = 12, $fn = 32);

        // Add string holes
        for (i = [-1, 1]) {
            translate([i * 30, -6, thickness/2])
            rotate([90, 0, 0])
            cylinder(h = thickness, r = 1.5, $fn = 16);
        }
    }
}

module decorative_beads() {
    // Top row - gold spacer beads
    for (i = [-3:3]) {
        translate([i * 10, collar_height - 3, thickness/2])
        color(gold_color)
        sphere(r = bead_size * 0.8, $fn = 12);
    }

    // Second row - alternating colors
    for (i = [-3:2]) {
        translate([i * 10 + 5, collar_height - 7, thickness/2])
        color(i % 2 == 0 ? carnelian : lapis)
        sphere(r = bead_size, $fn = 8);
