    for (i = [-5:5]) {
        translate([i * 6, collar_height - 18, thickness/2])
        union() {
            color(lapis)
            sphere(r = bead_size * 0.7, $fn = 8);
            translate([0, -bead_size, 0])
            color(gold_color)
            cylinder(h = bead_size * 2, r = 0.5, $fn = 8);
        }
    }
}

