// Historical Roman Glass Gaming Die (Tessera) - Solitaire Artifact
// Based on 1st-2nd century Roman dice used for gaming
// Features characteristic ring-and-dot pattern decoration

// Overall dimensions for 3D printing
$fn = 32;

// Main die body - slightly rounded edges for historical accuracy
module die_body() {
    color("LightSteelBlue")
    difference() {
        // Main cube with slightly rounded corners
        minkowski() {
            cube([16, 16, 16], center = true);
            sphere(1);
        }

        // Hollow out center for printability
        translate([0, 0, 0])
        cube([12, 12, 12], center = true);
    }
}

// Ring-and-dot pattern decoration characteristic of Roman dice
module ring_dot(position, size) {
    translate(position) {
        // Outer ring
        color("SteelBlue")
        cylinder(h = 1.5, r = size, center = true);

        // Inner dot
        color("MidnightBlue")
        cylinder(h = 2, r = size * 0.3, center = true);
    }
}

// Create face pattern based on Roman numeral value
module face_pattern(value, face_normal) {
    // Define positions for dots based on face value
    positions = [
        [], // 0 - shouldn't occur
        [[0, 0, 0]], // 1
        [[-4, -4, 0], [4, 4, 0]], // 2
        [[-4, -4, 0], [0, 0, 0], [4, 4, 0]], // 3
        [[-4, -4, 0], [4, -4, 0], [-4, 4, 0], [4, 4, 0]], // 4
        [[-4, -4, 0], [4, -4, 0], [0, 0, 0], [-4, 4, 0], [4, 4, 0]], // 5
        [[-4, -4, 0], [4, -4, 0], [-4, 0, 0], [4, 0, 0], [-4, 4, 0], [4, 4, 0]] // 6
    ];

    // Rotate to proper face
    rotate(face_normal) {
        translate([0, 0, 8.5]) {
            for(pos = positions[value]) {
                ring_dot(pos, 1.5);
            }
        }
    }
}

// Roman numeral inscriptions on edges
module edge_inscription() {
    color("Goldenrod")
    linear_extrude(height = 0.5) {
        // I-VI in Roman numerals on edges
        text("I", size = 2, halign = "center", valign = "center");
    }
}

// Create the complete die
