    union() {
        // Main body
        die_body();

        // Numbered faces (1-6)
        face_pattern(1, [0, 0, 0]); // Top face
        face_pattern(6, [180, 0, 0]); // Bottom face
        face_pattern(2, [90, 0, 0]); // Front face
        face_pattern(5, [-90, 0, 0]); // Back face
        face_pattern(3, [0, 90, 0]); // Right face
        face_pattern(4, [0, -90, 0]); // Left face

        // Decorative edge bevels
        color("SkyBlue")
        for(i = [-1, 1]) {
            for(j = [-1, 1]) {
                translate([i*7.5, j*7.5, 7.5])
                rotate([0, 90, 0])
                cylinder(h = 1, r = 1);
            }
        }
    }
}

