// Individual oil lamp module
module oil_lamp(diameter, rotation) {
    // Main body - elliptical shape
    scale([1.3, 1, 0.6])
        sphere(d = diameter, $fn = 32);

    // Top surface detail
    translate([0, 0, diameter*0.3])
    difference() {
        scale([1.2, 0.9, 0.2])
            sphere(d = diameter, $fn = 32);

        // Central filling hole
        translate([0, diameter*0.2, -0.5])
            cylinder(h = diameter*0.5, d = diameter*0.3, $fn = 16);

        // Nozzle opening
        translate([diameter*0.5, 0, -0.5])
            rotate([0, 0, rotation])
            cylinder(h = diameter*0.5, d1 = diameter*0.4, d2 = diameter*0.2, $fn = 8);
    }

    // Handle loop
    translate([-diameter*0.6, 0, diameter*0.2])
    rotate([90, 0, 0])
    difference() {
        rotate_extrude($fn = 16)
            translate([diameter*0.2, 0, 0])
            circle(d = diameter*0.15, $fn = 8);

        // Hollow handle
        rotate_extrude($fn = 16)
            translate([diameter*0.2, 0, 0])
            circle(d = diameter*0.08, $fn = 8);
    }

    // Decorative ring around top
    for(i = [0:30:330]) {
        rotate([0, 0, i])
        translate([diameter*0.55, 0, diameter*0.3])
        cylinder(h = diameter*0.05, d = diameter*0.08, $fn = 8);
    }

    // Base ring for stability
    difference() {
        translate([0, 0, -diameter*0.3])
        scale([1.25, 0.95, 0.1])
            sphere(d = diameter, $fn = 32);

        translate([0, 0, -diameter*0.29])
        scale([1.1, 0.8, 0.12])
            sphere(d = diameter*0.9, $fn = 32);
    }
}

