// Create the ring band
module ring_band() {
    difference() {
        // Outer cylinder
        cylinder(h=bezel_height, d=bezel_diameter, $fn=64);

        // Inner hole (slightly smaller for comfortable fit)
        translate([0, 0, -0.1])
            cylinder(h=bezel_height+0.2, d=bezel_diameter-2*bezel_thickness, $fn=64);

        // Decorative notches on top edge
        for(i=[0:7]) {
            rotate([0, 0, i*45])
                translate([bezel_diameter/2 - bezel_thickness/2, 0, bezel_height-1])
                    cube([bezel_thickness, 2, 1], center=true);
        }
    }
}

// Create the scarab body
module scarab_body() {
    color("darkgreen")
    difference() {
        // Main scarab body
        scale([1, 0.7, 0.5])
            sphere(d=scarab_length, $fn=32);

        // Head protrusion
        translate([scarab_length/3, 0, scarab_height/2])
            scale([0.3, 0.4, 0.3])
                sphere(d=scarab_length/3, $fn=16);

        // Wing lines decoration
        for(i=[-1:1]) {
            rotate([0, 0, i*30])
                translate([0, 0, scarab_height/2])
                    cube([scarab_length*0.8, 0.5, 0.5], center=true);
        }
    }
}

// Create the bezel mount
module bezel_mount() {
    color("bronze")
    difference() {
        union() {
            // Ring band
            ring_band();

            // Mounting platform for scarab
            translate([0, 0, bezel_height])
                cylinder(h=scarab_height/2, d=scarab_length+4, $fn=32);

            // Decorative lotus petals around mount
            for(i=[0:7]) {
                rotate([0, 0, i*45])
                    translate([(scarab_length+4)/2, 0, bezel_height+scarab_height/4])
                        scale([0.5, 0.3, 1])
