// Historical Bronze Age Pronged Artifact (c. 1200-800 BCE)
// A ritual prong with three tines used in ceremonial contexts

$fn = 32;

// Main parameters
prong_length = 70;
prong_width = 12;
shaft_diameter = 8;
base_height = 15;

module prong_tine(length, width, thickness) {
    // Create a single tine with tapered end
    difference() {
        // Main tine body
        hull() {
            translate([0, 0, 0]) 
                cube([thickness, width * 0.8, length * 0.9], center = true);
            translate([0, 0, length * 0.45]) 
                cube([thickness * 0.6, width * 0.4, length * 0.1], center = true);
        }

        // Add slight curve to tine tip
        translate([0, 0, length * 0.5])
            rotate([0, 90, 0])
                cylinder(h = thickness * 1.2, r = length * 0.15, center = true);
    }
}

module decorative_ring(height, diameter) {
    // Decorative ring with dot pattern
    difference() {
        cylinder(h = height, r = diameter/2, center = true);
        cylinder(h = height + 0.2, r = diameter/2 - 1.5, center = true);
    }

    // Dot pattern around ring
    for(i = [0:15]) {
        rotate([0, 0, i * 22.5])
            translate([diameter/2 - 0.75, 0, 0])
                sphere(r = 0.8);
    }
}

module shaft() {
    // Main shaft with grip texture
    color("Bronze")
    difference() {
        cylinder(h = prong_length * 0.6, r = shaft_diameter/2, center = true);

        // Hollow shaft for weight reduction
        translate([0, 0, -prong_length * 0.31])
            cylinder(h = prong_length * 0.62, r = shaft_diameter/2 - 2, center = true);
    }

    // Grip rings
    for(i = [1:3]) {
        translate([0, 0, -prong_length * 0.25 + i * prong_length * 0.15])
            decorative_ring(3, shaft_diameter + 2);
    }
}

module prong_base() {
    // Base with three mounting points for tines
    color("Bronze")
    difference() {
        cylinder(h = base_height, r1 = shaft_diameter/2 + 3, r2 = shaft_diameter/2 + 8, center = true);
        translate([0, 0, base_height/2 + 0.1])
            cylinder(h = base_height/2 + 1, r1 = shaft_diameter/2 - 1, r2 = shaft_diameter/2 - 1, center = true);
    }

    // Decorative triangles around base
    for(i = [0:5]) {
        rotate([0, 0, i * 60])
            translate([shaft_diameter/2 + 5.5, 0, base_height/4])
                rotate([0, 90, 0])
                    linear_extrude(height = 0.5)
                        polygon([[0,0], [2,1], [0,2]]);
    }
}

module full_prong() {
    // Assemble the complete artifact
    union() {
        // Shaft
        translate([0, 0, -prong_length/2 + base_height/2])
            shaft();

        // Base
        translate([0, 0, base_height/2])
            prong_base();

        // Three tines at 120 degree intervals
        for(i = [0:2]) {
            rotate([0, 0, i * 120])
                translate([0, shaft_diameter/2 + 2, base_height])
                    rotate([0, -10, 0])
                        prong_tine(prong_length * 0.4, prong_width/2, 3);
        }

        // Central decorative element
        translate([0, 0, base_height + prong_length * 0.2])
            sphere(r = 2);
    }
}

// Render the artifact
full_prong();

