// Ancient Egyptian Scarab Dangle Amulet
// Based on New Kingdom period scarabs (1550-1070 BCE)

// Color definitions
color_bronze = [150/255, 90/255, 40/255];
color_gold = [212/255, 175/255, 55/255];
color_stone = [180/255, 170/255, 150/255];

// Main parameters
total_length = 65;
scarab_size = 18;
loop_diameter = 8;
loop_thickness = 2;

// Create the dangle
union() {
    // Hanging loop
    translate([0, 0, total_length - loop_diameter/2]) {
        rotate([90, 0, 0]) {
            difference() {
                torus(loop_diameter/2, loop_thickness, $fn=32);
                // Cut for opening
                translate([0, loop_diameter/2, 0]) {
                    cube([loop_diameter*2, loop_diameter, loop_thickness*2], center=true);
                }
            }
        }
    }

    // Connector bar
    color(color_bronze) {
        translate([0, 0, total_length - loop_diameter - 5]) {
            cylinder(h=5, d1=4, d2=2, $fn=16);
        }
    }

    // Scarab body
    color(color_gold) {
        translate([0, 0, total_length - loop_diameter - 5 - scarab_size/2]) {
            union() {
                // Main scarab body
                scale([1.2, 1, 0.7]) sphere(d=scarab_size, $fn=32);

                // Head
                translate([0, scarab_size/2 + 3, 0]) {
                    scale([0.6, 0.8, 0.6]) sphere(d=scarab_size/2, $fn=16);
                }

                // Wing cases
                wing_case(-30);
                wing_case(30);
            }
        }
    }

    // Hieroglyphic inscription on base
    color(color_stone) {
        translate([0, 0, total_length - loop_diameter - 5 - scarab_size*0.8]) {
            difference() {
                cube([scarab_size*1.3, scarab_size*1.8, 2], center=true);

                // Carved symbols
                for(i = [-2:2]) {
                    translate([i*4, 0, -0.5]) {
                        cylinder(h=2, d=2, $fn=6);
                    }
                }

                // Central sun disk
                translate([0, 6, -0.5]) {
                    cylinder(h=2, d=4, $fn=32);
                }
            }
        }
    }
}

// Module for wing cases
module wing_case(angle) {
    rotate([0, 0, angle]) {
        translate([scarab_size/3, 0, 0]) {
            scale([0.8, 0.3, 0.2]) {
                rotate([0, 90, 0]) {
                    cylinder(h=scarab_size/2, d=scarab_size/1.5, $fn=32);
                }
            }
        }
    }
}

// Module for torus shape (for hanging loop)
module torus(outer_radius, tube_radius, fn) {
    rotate_extrude($fn=fn) {
        translate([outer_radius, 0, 0]) {
            circle(r=tube_radius, $fn=fn);
        }
    }
}

