// Basic Roman chain link with characteristic oval shape
module roman_link() {
    difference() {
        // Outer oval shape
        scale([1.3, 1, 1])
            rotate_extrude(angle = 360, $fn = $fn)
            translate([link_outer_diameter/2, 0, 0])
            circle(r = link_thickness);

        // Inner opening
        scale([1.3, 1, 1])
            rotate_extrude(angle = 360, $fn = $fn)
            translate([link_inner_diameter/2, 0, 0])
            circle(r = link_thickness - 0.5);
