// Module for individual rope strand
module rope_strand(strand_diameter, length, twist_angle) {
    rotate([0, 0, twist_angle])
        for(i = [0:segment_length:length-segment_length]) {
            translate([0, 0, i])
                cylinder(h=segment_length, d=strand_diameter, $fn=8);
