// Module for main rope body (3-strand construction)
module rope_body(length, diameter) {
    union() {
        // Three twisted strands
        for(strand = [0:120:240]) {
            rope_strand(diameter/2.2, length, strand);
        }

        // Central core for structure
        cylinder(h=length, d=diameter/3, $fn=12);
    }
}

// Turk's Head Knot decorative element
module turks_head_knot(position) {
    translate([0, 0, position])
    difference() {
        // Main knot body
        union() {
            // Outer ring
            rotate_extrude($fn=32)
                translate([knot_diameter/2, 0, 0])
                    circle(r=rope_diameter/2, $fn=8);

            // Cross strands
