// Roman Legionary Link - Lorica Segmentata Hinge
// Historically accurate reproduction of a 1st-2nd century AD armor link
// Used to connect overlapping steel plates of Roman segmented armor

$fn = 48;  // Smooth curves for 3D printing
wall_thickness = 2.5;  // Minimum printable wall thickness

// Main dimensions (historically accurate proportions)
link_length = 65;  // mm
link_width = 35;   // mm
link_thickness = 8;  // mm
hinge_diameter = 12;  // mm
ring_diameter = 22;  // mm

// Decorative elements
border_width = 3;
rivet_diameter = 4;

module roman_rivet(x, y) {
    // Decorative brass rivets used in period armor
    translate([x, y, link_thickness/2 + 1]) {
        color([0.8, 0.6, 0.2])  // Brass color
        union() {
            cylinder(h=2, d=rivet_diameter, center=true);
            translate([0, 0, 1]) 
                sphere(d=rivet_diameter);
        }
    }
}

module decorative_border() {
    // Raised border typical of Roman metalwork
    color([0.7, 0.7, 0.7])  // Iron color
    translate([0, 0, link_thickness/2 - border_width/2]) {
        difference() {
            cube([link_length, link_width, border_width], center=true);
            cube([link_length - border_width*2, link_width - border_width*2, border_width + 0.1], center=true);
        }
    }
}

module hinge_loop(side) {
    // Hinge loops for leather thong attachment
    // Historical loops were forged from wrought iron
    translate([side * link_length/2 - side*2, 0, 0]) {
        color([0.65, 0.65, 0.65])  // Dark iron
        rotate([90, 0, 0]) {
            difference() {
                cylinder(h=8, d=hinge_diameter, center=true);
                translate([0, 0, -4.1]) 
                    cube([hinge_diameter*0.8, hinge_diameter*0.8, 8.2], center=true);
            }
        }
    }
}

module attachment_ring(side) {
    // Central ring for attaching to armor plates
    translate([side * link_length/4, 0, 0]) {
        color([0.7, 0.7, 0.7])  // Iron color
        rotate([90, 0, 0]) {
            difference() {
                cylinder(h=link_thickness, d=ring_diameter, center=true);
                cylinder(h=link_thickness + 0.2, d=ring_diameter - wall_thickness*2, center=true);
            }
        }
    }
}

module roman_inscription() {
    // Simplified SPQR inscription (Senatus Populusque Romanus)
    translate([0, 0, link_thickness/2 + 0.1]) {
        color([0.1, 0.1, 0.1])  // Engraved black
        for(i = [-1:1]) {
            translate([i*8, 0, 0]) {
                cube([3, 2, 0.2], center=true);
            }
        }
    }
}

module lorica_link() {
    // Main link body - forged iron construction
    color([0.75, 0.75, 0.75])  // Weathered iron
    difference() {
        // Main plate
        cube([link_length, link_width, link_thickness], center=true);

        // Rounded ends for safety and comfort
        translate([link_length/2 - link_width/2, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=link_width, d=link_width, center=true);
        translate([-link_length/2 + link_width/2, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=link_width, d=link_width, center=true);
    }

    // Add decorative elements
    decorative_border();

    // Historical attachment points
    hinge_loop(1);    // Right side
    hinge_loop(-1);   // Left side
    attachment_ring(1);   // Right center
    attachment_ring(-1);  // Left center

    // Period-appropriate rivets
    roman_rivet(link_length/3, link_width/3);
    roman_rivet(-link_length/3, link_width/3);
    roman_rivet(link_length/3, -link_width/3);
    roman_rivet(-link_length/3, -link_width/3);

    // Roman inscription
    roman_inscription();
}

// Generate the complete link
lorica_link();

