    rotate([0, 0, angle])
    color("saddlebrown")
    linear_extrude(height = 2.5, scale = 0.5) 
    polygon(points = [[0, 0], [2, 1], [4, 0], [2, -1]]);
}

/* ===== Stylus impressions  front side ===== */
module front_inscriptions() {
