        for (col = [0:7]) {
            wedge(-30 + col * 8, -20 + row * 8, 90);
        }
}

/* ===== Stylus impressions  back side ===== */
module back_inscriptions() {
    for (row = [0:3])
        for (col = [0:5]) {
            wedge(-25 + col * 10, 15 - row * 10, 0);
        }
}

/* ===== Reinforcing edge bands ===== */
