// Historical Rope Strand from Ancient Maritime Knotwork
// Based on 16th century ship rigging strands with Turk's head knot terminal

// Color definitions for realistic appearance
color_rope = [0.6, 0.4, 0.2]; // Weathered hemp color
color_knot = [0.45, 0.3, 0.15]; // Darker knot due to handling

// Overall dimensions (mm)
strand_length = 80;
strand_diameter = 6;
knot_diameter = 12;

// Render the complete strand artifact
module strand_artifact() {
    // Main rope strand with realistic twisted texture
    color(color_rope) {
        // Create twisted rope effect using stacked cylinders
        for(i = [0:5:strand_length-5]) {
            rotate([0, 0, i*3]) {
                translate([0, 0, i]) {
                    cylinder(h=5, d=strand_diameter, $fn=8);
                }
            }
        }

        // Terminal end with frayed appearance
        translate([0, 0, strand_length-2]) {
            cylinder(h=4, d=strand_diameter*1.1, $fn=8);
            for(j = [1:3]) {
                rotate([0, 0, j*120]) {
                    translate([strand_diameter*0.3, 0, strand_length]) {
                        rotate([90, 0, 0]) {
                            cylinder(h=8, d=1, $fn=6);
                        }
                    }
                }
            }
        }
    }

    // Turk's head knot at the working end
    color(color_knot) {
        translate([0, 0, -6]) {
            // Base knot ring
            rotate_extrude(angle=360, $fn=32) {
                translate([knot_diameter/2, 0, 0]) {
                    square([2, 2]);
                }
            }

            // Knot crossings - 3-pass Turk's head
            for(k = [0:11]) {
                rotate([0, 0, k*30]) {
                    translate([knot_diameter/2, 0, 0]) {
                        rotate([90, 0, 0]) {
                            cylinder(h=knot_diameter, d=2.5, $fn=8);
                        }
                    }
                }
            }

            // Decorative beads at knot intersections
            for(m = [0:3]) {
                rotate([0, 0, m*90]) {
                    translate([knot_diameter/2, 0, -3]) {
                        sphere(d=3, $fn=12);
                    }
                }
            }
        }
    }

    // Historical maker's mark - carved initial "S"
    color([0.3, 0.2, 0.1]) {
        translate([0, strand_diameter/2, strand_length/2]) {
            rotate([90, 0, 0]) {
                linear_extrude(height=0.5) {
                    // Simple S shape
                    offset(r=0.5) {
                        text("S", size=3, font="Liberation Sans:style=Bold");
                    }
                }
            }
        }
    }

    // Age/wear marks along the strand
    color([0.35, 0.25, 0.15]) {
        for(w = [1:3]) {
            translate([0, -strand_diameter/2, w*20+10]) {
                rotate([90, 0, 0]) {
                    cylinder(h=strand_diameter*1.2, d=0.5, $fn=6);
                }
            }
        }
    }
}

// Create the complete artifact
strand_artifact();

// Ancient Egyptian Stringed Instrument - A Sistrum
// A sacred musical instrument used in ancient Egypt, associated with the goddess Hathor
// Features the characteristic U-shaped frame with crossbars and jingling metal disks

$fn = 50;

// Overall dimensions - approximately 80mm tall
staff_height = 80;
frame_width = 35;
frame_thickness = 3;

// Colors for realistic appearance
color_bronze = [0.8, 0.5, 0.2];
color_gold = [0.83, 0.69, 0.22];
color_dark_bronze = [0.6, 0.4, 0.15];

// Main handle/stound
module handle() {
    color(color_bronze)
    union() {
        // Main cylindrical handle
        cylinder(h = staff_height * 0.65, r = 4, center = false);

        // Decorative rings on handle
        for (i = [1:3:10]) {
            translate([0, 0, i * 5])
            cylinder(h = 1.5, r = 4.5, center = false);
        }

        // Lotus flower at base
        translate([0, 0, -2])
        color(color_gold)
        cylinder(h = 3, r1 = 5, r2 = 7, center = false);
    }
}

// U-shaped frame with Hathor head decoration
module frame() {
    color(color_bronze)
    union() {
        // Left side of U-frame
        translate([-frame_width/2, 0, staff_height * 0.65])
        rotate([0, 90, 0])
        cylinder(h = frame_width, r = frame_thickness, center = true);

        // Right side of U-frame
        translate([frame_width/2, 0, staff_height * 0.65])
        rotate([0, 90, 0])
        cylinder(h = frame_width, r = frame_thickness, center = true);

        // Crossbar
        translate([0, 0, staff_height * 0.65])
        rotate([90, 0, 0])
        cylinder(h = frame_width, r = frame_thickness, center = true);

        // Hathor head at top
        translate([0, 0, staff_height * 0.65 + frame_width/2])
        color(color_gold)
        union() {
            // Face outline
            sphere(r = 8);

            // Cow ears
            translate([-6, 0, 3])
            rotate([0, 30, 0])
            scale([1, 0.5, 1.5])
            sphere(r = 4);
            translate([6, 0, 3])
            rotate([0, -30, 0])
            scale([1, 0.5, 1.5])
            sphere(r = 4);

            // Curved horns
            translate([-4, -8, 3])
            rotate([0, 45, -30])
            scale([0.5, 0.5, 1])
            cylinder(h = 8, r1 = 3, r2 = 1, center = false);
            translate([4, -8, 3])
            rotate([0, 45, 30])
            scale([0.5, 0.5, 1])
            cylinder(h = 8, r1 = 3, r2 = 1, center = false);
        }
    }
}

// Jingling metal disks (the "strings" that create sound)
module disks() {
    color(color_gold)
    for (i = [-1:1]) {
        translate([i * 10, 0, staff_height * 0.65])
        rotate([90, 0, 0])
        difference() {
            cylinder(h = 1, r = 4, center = true);
            translate([0, 0, -0.5])
            cylinder(h = 2, r = 2, center = true);
        }
    }
}

// Decorative hieroglyphic bands
module hieroglyphs() {
    color(color_dark_bronze)
    for (i = [1:5:staff_height * 0.5]) {
        translate([0, 0, i])
        difference() {
            cylinder(h = 2, r = 4.2, center = true);
            // Eye of Horus pattern
            translate([-1.5, 0, 0])
            sphere(r = 0.5);
            translate([1.5, 0, 0])
            sphere(r = 0.5);
            translate([0, -1, 0])
            cube([3, 0.5, 2], center = true);
        }
    }
}

// Assembly
union() {
    handle();
    frame();
    disks();
    hieroglyphs();
}

// Bronze Age torc (neck ring) with spiral terminals, Ireland c.1200-800 BCE
// Designed for 3D printing: no large overhangs, 2 mm wall minimum

$fn = 40;  // smooth curves for realistic appearance
thickness = 4;        // torc band diameter
band_radius = 22;     // inner radius of neck opening
spiral_turns = 3.5;   // classic spiral count
spiral_radius = 6;    // spiral wire radius

module torc_band() {
    // central C-shaped band
    difference() {
        // outer torus segment
        rotate([0, 90, 0])
            rotate_extrude(angle = 220, $fn = 100)
                translate([band_radius, 0, 0])
                    circle(d = thickness);

        // inner hollow for 3D printing
        rotate([0, 90, 0])
            rotate_extrude(angle = 220, $fn = 100)
                translate([band_radius, 0, 0])
                    circle(d = thickness - 2);
    }
}

module spiral_terminal(offset_angle = 0) {
    // decorative spiral end typical of Bronze Age torcs
    color("bronze") 
    for (i = [0:36]) {
        angle = i * 10;
        radius = spiral_radius + (i * 0.25);
        height = (i / 36) * 8;

        translate([
            cos(angle + offset_angle) * radius,
            0,
            sin(angle + offset_angle) * radius + height
        ])
            sphere(d = 3.5);
    }

    // central boss
    translate([0, 0, 8])
        cylinder(h = 3, d = 8, $fn = 32);
}

module decorative_ribbing() {
    // raised rib decoration along band
    for (a = [30:30:190]) {
        rotate([0, 0, a])
            translate([band_radius, -thickness/2, 0])
                cube([thickness, thickness/2, 2]);
    }
}

// Main assembly
union() {
    color("bronze") torc_band();

    // left spiral terminal
    translate([band_radius * cos(220), 0, band_radius * sin(220)])
        rotate([90, 0, 220])
            spiral_terminal(0);

    // right spiral terminal
    translate([band_radius, 0, 0])
        rotate([90, 0, 0])
            spiral_terminal(180);

    decorative_ribbing();
}

// Iron Age Celtic Torc (neck ring) - Medium complexity realistic replica
// Based on Snettisham Great Torc style (1st century BCE)

// Main parameters
torc_diameter = 60;
wire_thickness = 6;
terminal_length = 25;
terminal_width = 15;
terminal_thickness = 8;

// Colors
bronze_color = "#CD7F32";
patina_color = "#4A5D23";

module torc_wire() {
    // Create the main twisted wire effect using multiple cylinders
    difference() {
        union() {
            // Main ring body
            rotate_extrude(angle=350, $fn=100)
                translate([torc_diameter/2, 0, 0])
                    circle(d=wire_thickness, $fn=8);

            // Add slight bulge for realistic wire twist
            for(i=[0:30:330]) {
                rotate([0, 0, i])
                    translate([torc_diameter/2, 0, 0])
                        sphere(d=wire_thickness*1.1, $fn=12);
            }
        }

        // Remove small gap for opening (typical torc design)
        translate([0, -torc_diameter/2-5, -wire_thickness])
            cube([torc_diameter+10, torc_diameter+10, wire_thickness*2]);
    }
}

module terminal_decoration() {
    // Classic Celtic trumpet spiral motif
    union() {
        // Main terminal body
        color(bronze_color)
            difference() {
                cube([terminal_length, terminal_width, terminal_thickness], center=true);

                // Hollow center
                cube([terminal_length-4, terminal_width-4, terminal_thickness+2], center=true);
            }

        // Trumpet spiral decorations
        for(i=[0:60:300]) {
            rotate([0, 0, i])
                translate([terminal_length/2-2, 0, 0])
                    color(bronze_color)
                        cylinder(h=terminal_thickness-1, d1=8, d2=3, center=true, $fn=20);
        }

        // Central boss
        translate([0, 0, terminal_thickness/2])
            color(bronze_color)
                sphere(d=6, $fn=20);
    }
}

module patina_effect() {
    // Add subtle weathering/patina
    for(i=[0:45:315]) {
        rotate([0, 0, i])
            translate([torc_diameter/2 + sin(i*5)*2, cos(i*5)*2, 0])
                color(patina_color)
                    sphere(d=1.5, $fn=8);
    }
}

// Main assembly
union() {
    // Main torc body
    color(bronze_color)
        torc_wire();

    // Left terminal
    translate([torc_diameter/2, 0, 0])
        rotate([90, 0, 0])
            terminal_decoration();

    // Right terminal
    translate([-torc_diameter/2, 0, 0])
        rotate([90, 0, 180])
            terminal_decoration();

    // Add patina/aging effect
    patina_effect();
}

// OpenSCAD Code: Celtic Bronze Mirror (Historical Circle Artifact)
