    difference() {
        // Solid bronze disk
        cylinder(h = mirror_thickness, d = mirror_diameter, center = true);

        // Reflective surface depression
        translate([0, 0, -0.5])
            cylinder(h = 1, d = mirror_diameter - 4, center = true);
    }

    // Decorative raised border
    translate([0, 0, 1.5])
        cylinder(h = 1.5, d1 = mirror_diameter, d2 = mirror_diameter - 2);

    // Celtic La Tne style decorations on back
    translate([0, 0, -mirror_thickness/2 - 0.5])
    union() {
        // Central boss
        cylinder(h = 2, d = 12);

        // Spiral decorations
        for (i = [0:3]) {
            rotate([0, 0, i * 90])
            translate([20, 0, 0])
