        cylinder(h = ring_height + 1, d = ring_inner_d, center = true);
    }

    // Decorative shoulders
    for (i = [0, 120, 240]) {
        rotate([0, 0, i]) translate([ring_outer_d/2, 0, 0]) {
            rotate([0, 90, 0]) 
            cylinder(h = 6, d1 = 4, d2 = 2, center = true);
        }
    }

    // Marquise stone setting
    translate([0, 0, ring_height/2 + stone_height/4]) {
        // Prongs (4 total)
        for (angle = [45, 135, 225, 315]) {
            rotate([0, 0, angle]) translate([stone_width/3, 0, 0]) {
                cylinder(h = stone_height/2, d = 1.5, center = true);
            }
        }

        // Stone base
        color([0.1, 0.1, 0.1]) // Dark stone color
        scale([1, stone_width/stone_length, 1]) 
        sphere(d = stone_length, center = true);

        // Stone point
        translate([0, 0, stone_height/4]) {
            rotate_extrude() {
