// Module for creating spiral patterns
module spiral_pattern(radius, segments, height) {
    for (i = [0:segments-1]) {
        angle = i * (360/segments);
        x = radius * cos(angle);
        y = radius * sin(angle);

        translate([x, y, height/2]) 
        rotate([0, 0, angle + 45]) 
        scale([1, 0.5, 1]) 
        cylinder(h = height, d = 3, center = true);
    }
}

