// Emerald Scarab Amulet - Ancient Egyptian Revival
// Based on Middle Kingdom scarab amulets (c. 2055-1650 BCE)

$fn = 50;

// Main dimensions
scarab_length = 45;
scarab_width = 30;
scarab_height = 15;
base_thickness = 3;
loop_diameter = 4;

// Color for emerald
color([0.1, 0.8, 0.4, 0.8]) {

// Base scarab body
difference() {
    union() {
        // Main oval body
        scale([1.2, 1, 0.8])
            sphere(d=scarab_width, $fn=50);

        // Head protrusion
        translate([scarab_length/2 - 5, 0, 0])
            sphere(d=scarab_width/2, $fn=30);
    }

    // Hollow bottom for 3D printing
    translate([0, 0, -scarab_height/2])
        cube([scarab_length, scarab_width, scarab_height], center=true);
}

// Wing cases detail
for(i = [-1, 1]) {
    translate([scarab_length/4, i*scarab_width/4, scarab_height/3])
        rotate([0, 0, i*15])
            scale([1.2, 0.3, 0.3])
                sphere(d=scarab_width/2);
}

// Head details
translate([scarab_length/2 - 3, 0, scarab_height/3])
    sphere(d=6);

// Eyes
for(i = [-1, 1]) {
    translate([scarab_length/2 - 2, i*5, scarab_height/2])
        sphere(d=3);
}

// Leg pairs (6 legs total)
module leg() {
    rotate([45, 0, 0])
        cylinder(h=8, d1=3, d2=1.5);
}

for(i = [0:2]) {
    angle = i*120;
    translate([scarab_length/4, 0, 0])
        rotate([0, 0, angle])
            translate([scarab_width/2 - 2, 0, 0])
                leg();
}

// Decorative hieroglyphic cartouche on back
translate([-scarab_length/4, 0, scarab_height/2 + 0.5])
    cube([15, 8, 1], center=true);

// Suspension loop for necklace
translate([-scarab_length/2, 0, 0])
    rotate([90, 0, 0])
        cylinder(h=loop_diameter*2, d=loop_diameter);

// Surface detailing - carved lines
for(i = [-2:2]) {
    if(i != 0) {
        translate([i*5, 0, scarab_height/2])
            cube([0.5, scarab_width/2, 0.5], center=true);
    }
}

// Wing markings
color([0.05, 0.4, 0.2, 0.6]) {
    for(i = [0:3]) {
        translate([-5 + i*8, 0, scarab_height/2])
            rotate([90, 0, 0])
                cylinder(h=0.3, r=2);
    }
}

}

// Historical Diamond Artifact - 16th Century Mughal Empire
// Inspired by the Koh-i-Noor diamond in its original cut form

// Colors for rendering
color_diamond = [0.9, 0.95, 1.0, 0.6];  // Slightly blue-tinted diamond
color_gold = [0.85, 0.65, 0.15, 1.0];    // Gold setting
color_enamel = [0.2, 0.3, 0.6, 1.0];     // Deep blue enamel

// Base dimensions
diamond_height = 40;
diamond_width = 35;
setting_thickness = 3;

// Main diamond crystal structure
module diamond_crystal() {
    color(color_diamond) {
        // Upper pyramid (crown)
        for (i = [0:3]) {
            rotate([0, 0, i * 90]) {
                translate([0, 0, diamond_height/2]) {
                    polyhedron(
                        points = [
                            [0, 0, diamond_height/2],
                            [diamond_width/2, 0, 0],
                            [0, diamond_width/2, 0],
                            [-diamond_width/2, 0, 0],
                            [0, -diamond_width/2, 0]
                        ],
                        faces = [
                            [0, 1, 2],
                            [0, 2, 3],
                            [0, 3, 4],
                            [0, 4, 1],
                            [1, 2, 3, 4]
                        ]
                    );
                }
            }
        }

        // Lower pyramid (pavilion)
        for (i = [0:3]) {
            rotate([0, 0, i * 90]) {
                translate([0, 0, -diamond_height/2]) {
                    polyhedron(
                        points = [
                            [0, 0, -diamond_height/2],
                            [diamond_width/2, 0, 0],
                            [0, diamond_width/2, 0],
                            [-diamond_width/2, 0, 0],
                            [0, -diamond_width/2, 0]
                        ],
                        faces = [
                            [0, 2, 1],
                            [0, 3, 2],
                            [0, 4, 3],
                            [0, 1, 4],
                            [1, 2, 3, 4]
                        ]
                    );
                }
            }
        }
    }
}

// Decorative gold setting with Mughal patterns
module gold_setting() {
    color(color_gold) {
        // Upper setting band
        translate([0, 0, diamond_height/2 + 2]) {
            difference() {
                cylinder(h = setting_thickness, r = diamond_width/2 + 4, $fn = 32);
                cylinder(h = setting_thickness + 1, r = diamond_width/2 + 1, $fn = 32);
            }
        }

        // Lower setting band
        translate([0, 0, -diamond_height/2 - 2]) {
            difference() {
                cylinder(h = setting_thickness, r = diamond_width/2 + 4, $fn = 32);
                cylinder(h = setting_thickness + 1, r = diamond_width/2 + 1, $fn = 32);
            }
        }

        // Prongs to hold diamond
        for (i = [0:3]) {
            rotate([0, 0, i * 90 + 45]) {
                translate([diamond_width/2 + 1, 0, 0]) {
                    cube([3, 3, diamond_height + 4], center = true);
                }
            }
        }

        // Decorative Mughal arches
        for (i = [0:7]) {
            rotate([0, 0, i * 45]) {
                translate([diamond_width/2 + 6, 0, diamond_height/4]) {
                    scale([1, 0.5, 1]) {
                        cylinder(h = 2, r = 4, $fn = 8);
                    }
                }
            }
        }
    }
}

// Enamel inscription band with Persian calligraphy pattern
module inscription_band() {
    color(color_enamel) {
        translate([0, 0, diamond_height/2 + 5]) {
            difference() {
                cylinder(h = 2, r = diamond_width/2 + 6, $fn = 64);
                cylinder(h = 3, r = diamond_width/2 + 5, $fn = 64);
            }

            // Decorative dots pattern inspired by Persian miniatures
            for (i = [0:15]) {
                rotate([0, 0, i * 22.5]) {
                    translate([diamond_width/2 + 5.5, 0, 1]) {
                        sphere(d = 1, $fn = 8);
                    }
                }
            }
        }
    }
}

// Historical wear and chips (realistic aging)
module historical_wear() {
    color(color_diamond) {
        // Small chips on edges
        for (i = [0:2]) {
            rotate([0, 0, i * 120]) {
                translate([diamond_width/2 - 2, 0, diamond_height/4]) {
                    sphere(d = 2, $fn = 8);
                }
                translate([diamond_width/3, 0, -diamond_height/4]) {
                    sphere(d = 1.5, $fn = 8);
                }
            }
        }
    }
}

// Assembly of the complete artifact
union() {
    diamond_crystal();
    gold_setting();
    inscription_band();
    historical_wear();
}

// Turquoise Aztec Mask (medium complexity, realistic style)
// Based on the Mixtec turquoise mosaic masks from Monte Albn, Oaxaca (14th-15th century)

color([0.2, 0.8, 0.8])  // Turquoise color

// Main mask face
module mask_face() {
    difference() {
        // Basic face shape - oval with flat back
        union() {
            // Main oval form
            scale([1.2, 1.4, 1]) 
                sphere(d=60, $fn=50);
            // Flatten back
            translate([0, 0, -29])
                cube([60, 84, 2], center=true);
        }

        // Eye holes
        translate([-15, 10, 10])
            rotate([0, 20, 0])
            cylinder(h=30, d1=12, d2=10, center=true, $fn=20);
        translate([15, 10, 10])
            rotate([0, -20, 0])
            cylinder(h=30, d1=12, d2=10, center=true, $fn=20);

        // Mouth opening
        translate([0, -20, 5])
            rotate([0, 10, 0])
            scale([1.5, 1, 1])
            cylinder(h=25, d=15, center=true, $fn=20);

        // Nostrils
        translate([-5, -5, 0])
            sphere(d=4, $fn=10);
        translate([5, -5, 0])
            sphere(d=4, $fn=10);
    }
}

// Turquoise mosaic tiles pattern
module mosaic_tiles() {
    // Forehead tiles
    for(y = [25:6:40]) {
        for(x = [-25:6:25]) {
            if(abs(x) + abs(y-32) < 30) {
                translate([x, y, 29.5])
                    rotate([90, 0, 0])
                    cylinder(h=0.5, d=5, $fn=6);
            }
        }
    }

    // Cheek tiles
    for(angle = [0:30:150]) {
        translate([30*cos(angle), 10+15*sin(angle), 29.5])
            rotate([90, 0, angle])
            cylinder(h=0.5, d=5, $fn=6);
    }
}

// Decorative serpent border
module serpent_border() {
    // Top border
    for(i = [-30:5:30]) {
        translate([i, 42, 28])
            rotate([0, 90, 0])
            cylinder(h=3, d1=4, d2=2, center=true, $fn=8);
    }

    // Side borders
    for(j = [30:5:42]) {
        translate([-36, j-21, 28])
            rotate([90, 0, 0])
            cylinder(h=3, d1=4, d2=2, center=true, $fn=8);
        translate([36, j-21, 28])
            rotate([90, 0, 0])
            cylinder(h=3, d1=4, d2=2, center=true, $fn=8);
    }
}

// Nose ornament
module nose_ornament() {
    union() {
        // Central bar
        translate([0, -3, 5])
            cube([3, 6, 8], center=true);

        // Side spirals
        for(side = [-1, 1]) {
            translate([side * 8, -3, 5])
                rotate([0, 0, side * 45])
                scale([1, 0.5, 1])
                cylinder(h=12, d1=8, d2=6, center=true, $fn=20);
        }
    }
}

// Back mounting holes for display
module mounting_holes() {
    translate([-20, 0, -25])
        cylinder(h=10, d=6, center=true, $fn=20);
    translate([20, 0, -25])
        cylinder(h=10, d=6, center=true, $fn=20);
    translate([0, -15, -25])
        cylinder(h=10, d=6, center=true, $fn=20);
}

// Assemble the mask
union() {
    // Main face
    mask_face();

    // Mosaic details
    color([0.15, 0.75, 0.75])
        mosaic_tiles();

    // Gold decorative elements
    color([0.8, 0.7, 0.2]) {
        serpent_border();
        nose_ornament();
    }

    // Cut mounting holes in back
    color([0.2, 0.8, 0.8])
        difference() {
            cube([0.1, 0.1, 0.1]);  // Dummy for difference
            mounting_holes();
        }
}

