// Ancient Sumerian Quartz Cylinder Seal (c. 2500 BCE)
// Mesopotamian administrative seal with cuneiform inscription and deity procession

$fn=64;

// Overall dimensions: 40mm height, 20mm diameter cylinder
module cylinder_seal() {
    color("cornsilk") difference() {
        // Main cylinder body
        cylinder(h=40, r=10, center=true);

        // Central boring hole (2mm diameter for string)
        rotate([90,0,0]) cylinder(h=25, r=1.2, center=true);

        // Registration notches for alignment
        translate([0,0,19]) cube([2,2,2], center=true);
        translate([0,0,-19]) cube([2,2,2], center=true);
    }
}

// Cuneiform inscription band
module cuneiform_inscription() {
    for(i=[-15:5:15]) {
        translate([0,0,i]) rotate([0,0,randi(0,360)]) {
            // Wedge impressions
            color("wheat") difference() {
                translate([9.5,0,0]) cube([1,0.5,1.5], center=true);
                translate([9.5,-0.5,0]) rotate([0,0,45]) cube([0.3,2,1.5], center=true);
            }
        }
    }
}

// Deity procession relief (Anu leading gods)
module deity_procession() {
    // Anu figure with horned crown
    color("antiquewhite") union() {
        translate([7,0,-10]) rotate([90,0,0]) cylinder(h=6, r1=2, r2=1);
        translate([7,0,-10]) rotate([90,0,0]) cylinder(h=3, r=0.5);
        // Horned crown
        for(a=[-30:30:30]) {
            rotate([0,0,a]) translate([7,2,-10]) rotate([90,0,0]) cylinder(h=1.5, r=0.3);
        }
    }

    // Attendant god figure
    color("antiquewhite") translate([7,0,0]) rotate([90,0,0]) cylinder(h=5, r1=1.5, r2=0.8);
    translate([7,0,0]) rotate([90,0,0]) cube([1,1,2], center=true);

    // Sacred bull symbol
    color("antiquewhite") translate([7,0,10]) rotate([90,0,0]) cylinder(h=4, r1=2, r2=1.5);
    // Bull horns
    rotate([0,0,-20]) translate([7,1.5,10]) rotate([90,0,0]) cylinder(h=2, r=0.2);
    rotate([0,0,20]) translate([7,-1.5,10]) rotate([90,0,0]) cylinder(h=2, r=0.2);
}

// Decorative border pattern - guilloche (twisted rope design)
module border_pattern() {
    for(t=[0:15:360]) {
        rotate([0,0,t]) {
            color("navajowhite") translate([9*cos(t*2), 9*sin(t*2), 18]) 
                sphere(r=0.5);
            color("navajowhite") translate([9*cos(t*2), 9*sin(t*2), -18]) 
                sphere(r=0.5);
        }
    }
}

// Base pedestal for display
module display_base() {
    color("tan") difference() {
        cylinder(h=5, r1=15, r2=12);
        translate([0,0,2]) cylinder(h=5, r1=12, r2=9);
    }
}

// Main assembly
union() {
    // Position seal on base
    translate([0,0,7.5]) cylinder_seal();

    // Add relief decorations
    deity_procession();
    cuneiform_inscription();
    border_pattern();

    // Display base
    translate([0,0,-7.5]) display_base();
}

// Agate Historical Artifact - Neo-Babylonian Agate Cylinder Seal (c. 600 BCE)
// Features traditional contest scene with hero, lions, and cuneiform inscription
// Designed for 3D printing with 2mm wall thickness and no overhangs

$fn = 50;

// Main dimensions
seal_diameter = 25;
seal_length = 40;
bore_diameter = 8;
bore_length = 38;

// Decorative band parameters
band_width = 4;
band_offset = 5;

// Create the agate cylinder seal
module agate_seal() {
    // Base cylinder body with slight taper for authenticity
    color("burlywood") 
    difference() {
        // Main cylinder with gentle taper
        cylinder(h = seal_length, d1 = seal_diameter, d2 = seal_diameter * 0.9, center = true);

        // Central bore for stringing
        translate([0, 0, -1])
            cylinder(h = bore_length + 2, d = bore_diameter, center = true);
    }

    // End caps with decorative ridges
    translate([0, 0, seal_length/2 - 1])
        color("peru") 
        cylinder(h = 2, d = seal_diameter + 2, center = false);

    translate([0, 0, -seal_length/2 + 1])
        color("peru") 
        cylinder(h = 2, d = seal_diameter + 2, center = false);

    // Decorative bands around ends
    for (z = [-seal_length/2 + band_offset, seal_length/2 - band_offset - band_width]) {
        translate([0, 0, z])
            color("saddlebrown") 
            cylinder(h = band_width, d = seal_diameter + 0.5, center = false);
    }
}

// Hero figure with raised relief
module hero_figure() {
    // Hero body (profile view)
    translate([0, seal_diameter/2 - 1, 0])
        rotate([90, 0, 0])
            color("darkgoldenrod") 
            cylinder(h = 8, d = 3, center = true);

    // Hero head
    translate([0, seal_diameter/2 - 1, 2])
        sphere(d = 2.5);

    // Hero arms (raised in combat pose)
    translate([1.5, seal_diameter/2 - 1, 1])
        rotate([0, 45, 0])
            cube([1, 1, 3], center = true);

    translate([-1.5, seal_diameter/2 - 1, 1])
        rotate([0, -45, 0])
            cube([1, 1, 3], center = true);
}

// Lion figure facing hero
module lion_figure() {
    // Lion body
    translate([8, seal_diameter/2 - 1, 0])
        rotate([90, 0, 180])
            color("darkgoldenrod") 
            cylinder(h = 6, d = 2.5, center = true);

    // Lion head with mane
    translate([10, seal_diameter/2 - 1, 0])
        sphere(d = 3);

    // Lion legs
    for (x = [7.5, 8.5]) {
        translate([x, seal_diameter/2 - 1, -2])
            cube([0.5, 0.5, 2], center = true);
    }
}

// Cuneiform inscription band
module cuneiform_inscription() {
    // Simplified cuneiform wedges arranged in a band
    for (i = [0:5:35]) {
        angle = i * 10;
        for (j = [0:2:6]) {
            rotate([0, 0, angle])
                translate([seal_diameter/2 - 0.5, 0, -seal_length/2 + 5 + j])
                    color("saddlebrown") 
                    cube([1, 0.5, 0.5], center = true);
        }
    }
}

// Background decorative pattern
module background_pattern() {
    // Dot pattern typical of Mesopotamian seals
    for (angle = [0:30:360]) {
        rotate([0, 0, angle])
            for (z = [-seal_length/2 + 8:2:seal_length/2 - 8]) {
                translate([seal_diameter/2 - 1.5, 0, z])
                    sphere(d = 0.8);
            }
    }
}

// Main assembly
union() {
    // Base seal
    agate_seal();

    // Decorative elements arranged around cylinder
    for (rotation = [0, 120, 240]) {
        rotate([0, 0, rotation]) {
            hero_figure();
            lion_figure();
            background_pattern();
        }
    }

    // Central cuneiform inscription
    cuneiform_inscription();
}

// Optional display stand
translate([0, 0, -seal_length/2 - 5])
    color("gray") 
    cube([30, 10, 2], center = true);

// Beryl intaglio ring - Roman Empire, 1st-2nd century CE
// Carved beryl gemstone set in gold ring with decorative shoulders

$fn = 64;

// Overall scale
scale_factor = 1.2;

// Main beryl gemstone (hexagonal emerald shape)
module beryl_stone() {
    color([0.2, 0.8, 0.4], 0.8)  // Translucent green beryl
    difference() {
        // Hexagonal prism for the gemstone
        cylinder(h = 8 * scale_factor, r1 = 12 * scale_factor, r2 = 10 * scale_factor);

        // Top facet - table cut
        translate([0, 0, 7.5 * scale_factor])
            cylinder(h = 2 * scale_factor, r1 = 8 * scale_factor, r2 = 10 * scale_factor);

        // Pavilion facets
        for(i = [0:5]) {
            rotate([0, 0, i * 60])
                translate([6 * scale_factor, 0, 0])
                    rotate([0, 30, 0])
                        cube([12 * scale_factor, 0.5, 8 * scale_factor], center = true);
        }

        // Intaglio carving - image of Victory (Nike) engraved on base
        translate([0, 0, -0.5 * scale_factor])
            scale([0.8, 0.8, 1])
                cylinder(h = 1 * scale_factor, r = 8 * scale_factor);

        // Wing details in intaglio
        for(wing = [45, -45]) {
            rotate([0, 0, wing])
                translate([4 * scale_factor, 0, -0.3 * scale_factor])
                    scale([1, 0.3, 0.5])
                        sphere(3 * scale_factor);
        }
    }
}

// Gold ring shank
module ring_shank() {
    color("gold") {
        // Main ring band
        rotate_extrude(angle = 360, $fn = 100)
            translate([20 * scale_factor, 0, 0])
                circle(r = 3 * scale_factor);

        // Decorative shoulders with scrollwork
        for(side = [-1, 1]) {
            translate([side * 15 * scale_factor, 0, 0]) {
                // Scroll volutes
                rotate([90, 0, 90])
                    linear_extrude(height = 4 * scale_factor, twist = 180)
                        difference() {
                            circle(r = 5 * scale_factor);
                            circle(r = 3 * scale_factor);
                        }

                // Granulation pattern
                for(i = [0:4]) {
                    translate([side * i * 1.2 * scale_factor, 0, 0])
                        sphere(0.5 * scale_factor);
                }
            }
        }
    }
}

// Bezel setting for the beryl
module bezel_setting() {
    color("gold") {
        difference() {
            // Main bezel cup
            cylinder(h = 5 * scale_factor, r = 13 * scale_factor);

            // Hollow for stone
            cylinder(h = 6 * scale_factor, r = 11 * scale_factor);
        }

        // Prongs
        for(i = [0:5]) {
            rotate([0, 0, i * 60])
                translate([11.5 * scale_factor, 0, 2 * scale_factor])
                    cylinder(h = 4 * scale_factor, r = 1 * scale_factor);
        }
    }
}

// Assembly
union() {
    // Ring shank
    ring_shank();

    // Bezel setting
    translate([0, 0, 15 * scale_factor])
        rotate([90, 0, 0])
            bezel_setting();

    // Beryl gemstone in setting
    translate([0, 0, 18 * scale_factor])
        rotate([90, 0, 0])
            beryl_stone();
}

// Citrine Roman Intaglio Seal Ring - Historical Artifact
// Based on 1st-2nd century AD Roman signet rings
// Size: 20mm diameter stone, 60mm total length

$fn = 64;

// Main colors
stone_color = "#D4AF37";  // Natural citrine yellow
metal_color = "#B87333";  // Bronze
detail_color = "#8B4513"; // Darker bronze patina

// Ring band module
module ring_band(outer_r=12, inner_r=10, height=8) {
    difference() {
        cylinder(h=height, r=outer_r);
        translate([0, 0, -0.1]) cylinder(h=height+0.2, r=inner_r);
