gold_color = [0.8, 0.7, 0.2];
bronze_color = [0.5, 0.3, 0.1];

module scarab_body() {
    // Main oval body
    difference() {
        scale([1, 0.7, 0.5])
            sphere(d=scarab_length);

        // Flatten bottom
        translate([0, 0, -scarab_height/2])
            cube([scarab_length*2, scarab_width*2, scarab_height], center=true);
    }
}

module peridot_inlay() {
    // Peridot oval cabochon set in center
    color(peridot_color) {
        translate([0, 0, scarab_height/2 + 1])
            scale([1, 0.8, 0.3])
                sphere(d=scarab_width * 0.6);
    }

    // Gold bezel setting
    color(gold_color) {
        translate([0, 0, scarab_height/2 + 0.5])
            cylinder(h=2, d1=scarab_width*0.65, d2=scarab_width*0.7);
    }
}

module head_details() {
    // Head section
    translate([scarab_length/2 - 5, 0, 0])
        scale([0.3, 0.4, 0.3])
            sphere(d=scarab_width/2);

    // Antennae loops
    for(i = [-1, 1]) {
        translate([scarab_length/2 - 8, i * scarab_width/4, scarab_height/4])
            rotate([90, 0, 0])
                cylinder(h=8, d=2);
    }
}

module wing_cases() {
    // Elytra (wing cases) with decorative lines
    for(side = [-1, 1]) {
        // Main wing case
        translate([side * scarab_length/4, 0, 0])
            scale([0.4, 0.9, 0.3])
                sphere(d=scarab_width);

        // Decorative lines
        for(i = [0:4]) {
            translate([side * scarab_length/4, 0, scarab_height/2 - 1])
                rotate([0, 0, i * 15 * side])
                    cube([scarab_length/4, 1, 1], center=true);
        }
    }
}

module legs() {
    // Six legs in Egyptian style
    color(bronze_color) {
        for(side = [-1, 1]) {
            for(pos = [0.3, 0.6, 0.9]) {
                translate([scarab_length/2 - pos * scarab_length, side * scarab_width/2, -scarab_height/4])
                    rotate([0, 0, side * 45])
                        cylinder(h=8, d=3);
            }
        }
    }
}

module hieroglyphs() {
    // Simplified cartouche inscription on base
    translate([0, 0, -scarab_height/2 + 0.5])
        color(gold_color) {
            // Oval cartouche outline
            scale([1.2, 0.4, 0.1])
                sphere(d=scarab_width * 0.8);

            // Hieroglyph patterns
            for(i = [-1, 0, 1]) {
                translate([i * 8, 0, 0.5])
                    cube([2, scarab_width * 0.3, 1], center=true);
            }
        }
}

