    color(bronze_color) scarab_body();
    peridot_inlay();
    head_details();
    wing_cases();
    legs();
    hieroglyphs();
}

// Spinel historical artifact: Medieval French "Balas Ruby" Ring
// Based on the 14th century "Black Prince's Ruby" spinel in the Imperial State Crown

// Ring parameters
ring_outer_diameter = 22;
ring_inner_diameter = 18.5;
ring_width = 6;
ring_thickness = 2;

// Spinel stone parameters
spinel_width = 15;
spinel_length = 20;
spinel_height = 10;
table_size = 0.7;  // Proportion of table to girdle

// Setting parameters
setting_thickness = 1.5;
prong_height = 3;
prong_count = 8;
prong_width = 1.2;

// Decorative elements
fleur_height = 4;
fleur_width = 5;
band_pattern_segments = 12;

// Colors
spinel_color = [0.7, 0.1, 0.2];  // Deep red spinel
gold_color = [0.83, 0.69, 0.22];  // Medieval gold alloy
highlight_color = [0.9, 0.8, 0.3];  // Highlight for raised details

module ring_band() {
    difference() {
        // Main band
        color(gold_color)
        cylinder(h=ring_width, r1=ring_outer_diameter/2, r2=ring_outer_diameter/2, center=true);

        // Hollow center
        color(gold_color)
        cylinder(h=ring_width+1, r1=ring_inner_diameter/2, r2=ring_inner_diameter/2, center=true);
    }

    // Decorative pattern on band
    for (i=[0:band_pattern_segments-1]) {
        rotate([0, 0, i*360/band_pattern_segments])
        translate([ring_outer_diameter/2 - ring_thickness/2, 0, 0])
        rotate([90, 0, 0])
        color(highlight_color)
        scale([0.3, 0.3, 0.3])
        cylinder(h=ring_width*0.8, r=ring_thickness/2, $fn=6);
    }
}

module spinel_stone() {
    // Main stone body - octahedral shape
    color(spinel_color)
    scale([spinel_width/spinel_length, 1, spinel_height/spinel_length])
    polyhedron(
        points=[
            [0, 0, spinel_length/2],           // Table top
            [0, 0, -spinel_length/2],          // Culet bottom
            [table_size*spinel_length/2, table_size*spinel_length/2, spinel_length/2],
            [-table_size*spinel_length/2, table_size*spinel_length/2, spinel_length/2],
            [-table_size*spinel_length/2, -table_size*spinel_length/2, spinel_length/2],
            [table_size*spinel_length/2, -table_size*spinel_length/2, spinel_length/2],
            [spinel_length/2, spinel_length/2, -spinel_length/2 + spinel_length*0.8],
            [-spinel_length/2, spinel_length/2, -spinel_length/2 + spinel_length*0.8],
            [-spinel_length/2, -spinel_length/2, -spinel_length/2 + spinel_length*0.8],
            [spinel_length/2, -spinel_length/2, -spinel_length/2 + spinel_length*0.8]
        ],
        faces=[
            [0, 2, 3], [0, 3, 4], [0, 4, 5], [0, 5, 2],  // Table
            [1, 6, 7], [1, 7, 8], [1, 8, 9], [1, 9, 6],  // Bottom
            [2, 0, 5], [5, 9, 2], [2, 9, 6], [2, 6, 0],  // Side facets
            [3, 0, 2], [2, 6, 3], [3, 6, 7], [3, 7, 0],
            [4, 0, 3], [3, 7, 4], [4, 7, 8], [4, 8, 0],
            [5, 0, 4], [4, 8, 5], [5, 8, 9], [5, 9, 0]
        ]
    );
}

module stone_setting() {
    // Base bezel
    color(gold_color)
    translate([0, 0, -0.5])
    cylinder(h=setting_thickness, r1=spinel_length/2 + 2, r2=spinel_length/2 + 2, $fn=32);

    // Prongs
    for (i=[0:prong_count-1]) {
        rotate([0, 0, i*360/prong_count])
        translate([spinel_length/2 + 0.5, 0, setting_thickness/2])
        rotate([90, 0, 90])
        color(gold_color)
        cylinder(h=prong_height, r=prong_width/2, $fn=8);
    }

    // Decorative fleur-de-lis on sides
    for (i=[0:3]) {
        rotate([0, 0, i*90])
        translate([spinel_length/2 + 3, 0, setting_thickness + fleur_height/2])
        color(highlight_color)
        union() {
            // Central petal
            scale([0.5, 1, 1])
            cube([fleur_width/2, fleur_width/4, fleur_height], center=true);

            // Side petals
            translate([fleur_width/4, fleur_width/3, 0])
            rotate([0, 0, 30])
            scale([0.4, 0.8, 1])
            cube([fleur_width/2, fleur_width/4, fleur_height*0.8], center=true);

            translate([fleur_width/4, -fleur_width/3, 0])
            rotate([0, 0, -30])
            scale([0.4, 0.8, 1])
            cube([fleur_width/2, fleur_width/4, fleur_height*0.8], center=true);
        }
    }
}

