    color(Tourmaline_Dark) {
        // Front legs
        for(side = [-1, 1]) {
            translate([side*10, -15, -8]) {
                rotate([0, 45*side, 0]) {
                    rotate([90, 0, 0]) cylinder(h=12, d=3);
                }
            }
        }

        // Middle legs
        for(side = [-1, 1]) {
            translate([side*15, 0, -10]) {
                rotate([0, 30*side, 0]) {
                    rotate([90, 0, 0]) cylinder(h=15, d=2.5);
                }
            }
        }

        // Rear legs
        for(side = [-1, 1]) {
            translate([side*12, 15, -8]) {
                rotate([0, 15*side, 0]) {
                    rotate([90, 0, 0]) cylinder(h=12, d=3);
                }
            }
        }
    }
}

// Hieroglyphic inscription base
module inscription_base() {
    difference() {
        color(Gold) {
            cube([40, 25, 4], center=true);
        }

        // Decorative border pattern
        for(i = [0:7]) {
            rotate([0, 0, i*45]) {
                translate([19, 0, 1.5]) {
                    cube([2, 1, 2], center=true);
                }
            }
        }

        // Center hole for threading
        translate([0, 0, -2]) {
            cylinder(h=8, d=3);
        }
    }
}

// Cartouche detail on back
module cartouche_detail() {
    color(Gold) {
        translate([0, -5, 14]) {
            rotate([90, 0, 0]) {
                // Oval cartouche shape
                scale([1.5, 0.6, 0.5]) sphere(d=10);

                // Hieroglyph symbols
                translate([0, 0, 0.5]) {
                    // Ankh symbol
                    scale([0.3, 0.3, 0.3]) {
                        // Loop
                        rotate([0, 90, 0]) cylinder(h=2, d=4);
                        // Stem
                        translate([0, 0, -1]) cube([1, 2, 1], center=true);
                        // Cross
                        translate([0, 1.5, -1]) cube([3, 1, 1], center=true);
                    }
                }
            }
        }
    }
}

// Assembly
translate([0, 0, 5]) {
    // Base with inscription
    inscription_base();

    // Scarab body on top
    translate([0, 0, 6]) {
        // Main body
        scarab_body();

        // Legs attached
        scarab_legs();

        // Back details
        cartouche_detail();

        // Eye details
        for(side = [-1, 1]) {
            color([0, 0, 0]) {
                translate([side*3, -18, 4]) {
                    sphere(d=2);
                }
            }
        }
    }
}

// Malachite Egyptian Scarab Amulet - Historical Artifact (circa 1980 BCE)
// Medium complexity realistic model with proper 3D printing considerations

// Color constants for malachite
$fa = 1;
$fs = 0.4;

module scarab_body() {
    // Main scarab body - elytra (wing cases)
    color([0.0, 0.6, 0.2]) {
        difference() {
            // Central oval body
            scale([1.2, 1, 0.8]) sphere(d=20);

            // Head cavity
            translate([0, 8, 0]) 
                scale([0.4, 0.4, 0.6]) sphere(d=20);

            // Underside cavity for 3D printing
            translate([0, 0, -5])
                cube([15, 25, 10], center=true);
        }

        // Head section
        translate([0, 8, 3])
            scale([0.8, 0.6, 0.6]) sphere(d=10);
    }
}

module wing_details() {
    // Wing separation line
    color([0.0, 0.5, 0.15]) {
        translate([0, -2, 5])
            cube([1, 15, 0.5], center=true);

        // Wing vein patterns
        for(i = [-1, 1]) {
            translate([i * 3, 0, 5.5]) {
                for(j = [0:3]) {
                    rotate([0, 0, j * 20 - 30])
                        translate([0, j * 2, 0])
                            scale([0.2, 2, 0.2])
                                cylinder(h=0.5, d=1);
                }
            }
        }
    }
}

module legs() {
    // Six legs in Egyptian scarab style
    color([0.0, 0.5, 0.15]) {
        for(side = [-1, 1]) {
            for(pos = [0, 1, 2]) {
                translate([side * 8, -3 + pos * 5, -2]) {
                    rotate([0, 0, side * (30 - pos * 10)])
                        cylinder(h=5, d=1.5, $fn=6);

                    // Foot detail
                    translate([side * 3.5, -1, 0])
                        sphere(d=2);
                }
            }
        }
    }
}

module hieroglyphs_base() {
    // Base with hieroglyphic inscription
    color([0.0, 0.55, 0.18]) {
        translate([0, -5, -4])
            cube([18, 12, 2], center=true);

        // Simplified hieroglyph patterns (Ra and protection symbols)
        translate([0, -5, -2.5]) {
            // Ra sun disk
            color([0.1, 0.4, 0.1])
            translate([-5, 0, 0])
                cylinder(h=0.5, d=3);

            // Ankh symbols
            for(pos = [-2, 2, 5]) {
                translate([pos, 0, 0]) {
                    // Ankh loop
                    color([0.1, 0.4, 0.1])
                    rotate_extrude()
                        translate([1, 0, 0])
                            circle(d=2);

                    // Ankh stem
                    color([0.1, 0.4, 0.1])
                    translate([0, -2, 0])
                        cube([0.5, 4, 0.5], center=true);
                }
            }
        }
    }
}

module suspension_loop() {
    // Top loop for wearing as pendant
    color([0.0, 0.5, 0.15]) {
        translate([0, 10, 8])
            rotate([90, 0, 0])
                difference() {
                    torus(4, 1.5, $fn=20);
                    cube([8, 8, 3], center=true);
                }
    }
}

// Assembly
union() {
    scarab_body();
    wing_details();
    legs();
    hieroglyphs_base();
    suspension_loop();

    // Malachite banding pattern
    for(i = [0:5]) {
        color([0.0, 0.45 + i * 0.03, 0.15]) {
            rotate([0, 90, 0])
                translate([0, -10 + i * 3, 0])
                    scale([1.1 - i * 0.05, 1.1 - i * 0.05, 0.1])
                        cylinder(h=20, d=20);
        }
    }
}

// Obsidian Aztec Macuahuitl (Obsidian Sword)
// A realistic recreation of the Aztec weapon with obsidian blades

$fn = 50;

// Main blade parameters
blade_length = 80;
blade_width = 40;
blade_thickness = 8;
handle_length = 40;
handle_diameter = 15;

// Obsidian blade color
color([0.1, 0.1, 0.1, 0.9]) {
    // Main wooden paddle
    union() {
        // Central paddle
        translate([0, 0, 0]) {
            difference() {
                cube([blade_length, blade_width, blade_thickness], center = true);
                // Grooves for obsidian blades
                for (i = [-3:3]) {
                    translate([i * 10, 0, -blade_thickness/2 - 0.1]) {
                        cube([2, blade_width * 0.9, blade_thickness/2 + 0.2]);
                    }
                }
            }
        }

        // Handle
        translate([-blade_length/2 - handle_length/2, 0, 0]) {
            rotate([0, 90, 0]) {
                cylinder(h = handle_length, d = handle_diameter, center = true);
            }
        }

        // Handle guard
        translate([-blade_length/2 - 5, 0, 0]) {
            rotate([0, 90, 0]) {
                cylinder(h = 8, d = handle_diameter + 6, center = true);
            }
        }

        // Obsidian blades
        for (i = [-3:3]) {
            translate([i * 10, blade_width/2 - 2, 0]) {
                // Top blades
                rotate([0, 0, -15]) {
                    linear_extrude(height = 1) {
                        polygon(points = [
                            [0, 0],
                            [3, 2],
                            [0, 4],
                            [-1, 2]
                        ]);
                    }
                }
            }

            // Bottom blades
            translate([i * 10, -blade_width/2 + 2, 0]) {
                rotate([0, 0, 15]) {
                    linear_extrude(height = 1) {
                        polygon(points = [
                            [0, 0],
                            [3, -2],
                            [0, -4],
                            [-1, -2]
                        ]);
                    }
                }
            }
        }

        // Decorative inlays
        translate([0, 0, blade_thickness/2 + 0.1]) {
            for (i = [-2:2]) {
                for (j = [-1:1]) {
                    translate([i * 15, j * 10, 0]) {
                        color([0.8, 0.7, 0.4]) {
                            sphere(d = 2);
                        }
                    }
                }
            }
        }

        // Handle grip texture
        translate([-blade_length/2 - handle_length/2, 0, 0]) {
            rotate([0, 90, 0]) {
                for (i = [-5:5]) {
                    translate([i * 3, 0, handle_diameter/2 + 0.1]) {
                        cube([2, 1, 1], center = true);
                    }
                }
            }
        }
    }
}

