            for(i = [0:10]) {
                translate([sin(i*15)*i*2, cos(i*15)*i*2, i*7]) 
                    rotate([0, 0, i*15])
                    cylinder(h=8, d1=35-i*2.5, d2=33-i*2.5, $fn=6);
            }

