        translate([0, 0, -4])
            cylinder(h=8, d=38, d2=40, $fn=32);

        // Decorative ridges
        for(i = [0:3]) {
            translate([0, 0, -3+i*2])
                cylinder(h=1, d=42-i, $fn=32);
        }
