    color("goldenrod") {
        for(i = [1:3]) {
            translate([sin(i*50)*i*5, cos(i*50)*i*5, 15+i*15])
                rotate([0, 0, i*50])
                difference() {
                    cylinder(h=3, d=28-i, $fn=32);
