// Historical Shell Artifact - Ammonite Fossil Replica
// Based on Mesozoic era ammonite fossils (200-66 million years ago)
// This model represents a polished fossil specimen with characteristic spiral chambers

// Overall dimensions: 80mm diameter, 30mm thickness

// Main spiral shell with growth rings
module spiral_shell(turns=5, base_radius=40, growth_factor=1.2, thickness=8) {
    // Create the main spiral structure
    for (i = [0:turns*36]) {
        angle = i * 10;
        radius = base_radius * pow(growth_factor, i/36);

        // Each chamber segment of the spiral
        translate([cos(angle) * radius/2, sin(angle) * radius/2, 0])
        rotate([0, 0, angle])
        scale([1, 1.1, 1])  // Slightly elliptical cross-section
        cylinder(h=thickness, r1=radius/8, r2=radius/9, $fn=20);
    }
}

// Internal chamber walls (septa)
module chamber_walls(turns=5, base_radius=40) {
    for (i = [1:turns*6]) {
        angle = i * 60;
        radius = base_radius * 0.7 * pow(1.1, i/6);

        // Curved septa walls between chambers
        translate([cos(angle) * radius/2, sin(angle) * radius/2, -1])
        rotate([0, 0, angle + 30])
        scale([1, 0.3, 1])
        cylinder(h=10, r1=radius/4, r2=0, $fn=8);
    }
}

// Fossilized surface texture with growth lines
module growth_lines(turns=5, base_radius=40) {
    for (i = [0:turns*20]) {
        angle = i * 18;
        radius = base_radius * 0.8 * pow(1.15, i/20);

        // Fine ridges following growth pattern
        translate([cos(angle) * radius/2, sin(angle) * radius/2, 4])
        rotate([0, 0, angle])
        scale([1, 0.05, 0.3])
        cylinder(h=1, r=radius/6, $fn=6);
    }
}

// Central umbilicus (opening at center)
module central_opening(radius=8) {
    difference() {
        cylinder(h=10, r=radius, $fn=32);
        translate([0, 0, -1])
        cylinder(h=12, r=radius-2, $fn=32);
    }
}

// Fossil matrix base (stone surrounding fossil)
module fossil_base() {
    difference() {
        // Main oval base
        scale([1.2, 1, 0.5])
        cylinder(h=5, r=45, $fn=64);

        // Cutout for the fossil impression
        translate([0, 0, -1])
        scale([1.05, 0.9, 2])
        cylinder(h=10, r=42, $fn=64);
    }
}

// Assemble the complete fossil specimen
union() {
    // Stone matrix base - light gray limestone color
    color([0.7, 0.7, 0.65])
    fossil_base();

    // Main spiral shell - fossilized brown/gold color
    color([0.6, 0.5, 0.35])
    union() {
        spiral_shell();

        // Add chamber walls inside
        chamber_walls();

        // Surface texture details
        growth_lines();
    }

    // Central opening - darker showing depth
    color([0.4, 0.3, 0.2])
    translate([0, 0, 4])
    central_opening();

    // Edge details showing fossilization
    color([0.8, 0.75, 0.65])
    for (i = [0:45:360]) {
        rotate([0, 0, i])
        translate([38, 0, 2])
        scale([0.3, 0.3, 0.5])
        sphere(r=3, $fn=8);
    }
}

$fa = 2;
