// Filigree Byzantine Cross Pendant (9th-11th century style)
// Overall dimensions: ~60mm height x 40mm width x 4mm thickness

// Color definitions
$fn = 32;

// Main module
module byzantine_cross() {
    // Base cross shape (flat back plate)
    color("gold") difference() {
        // Main cross plate
        union() {
            // Vertical bar
            translate([0, 20, 0]) cube([4, 40, 2], center=true);
            // Horizontal bar
            translate([0, 0, 0]) cube([30, 4, 2], center=true);
        }

        // Cutout centers for filigree effect
        translate([0, 20, 0]) cube([2, 36, 3], center=true);
        translate([0, 0, 0]) cube([26, 2, 3], center=true);
    }

    // Decorative filigree patterns
    color("gold") {
        // Top decorative circle
        translate([0, 35, 1]) {
            cylinder(h=1, d1=8, d2=8);
            difference() {
                cylinder(h=1.1, d1=6, d2=6);
                cylinder(h=1.2, d1=4, d2=4);
            }
        }

        // Side decorative circles
        for(x = [-10, 10]) {
            translate([x, 0, 1]) {
                cylinder(h=1, d1=6, d2=6);
                difference() {
                    cylinder(h=1.1, d1=4, d2=4);
                    cylinder(h=1.2, d1=2, d2=2);
                }
            }
        }

        // Bottom decorative element
        translate([0, -25, 1]) {
            sphere(d=5);
            difference() {
                sphere(d=3.5);
                sphere(d=2);
            }
        }

        // Filigree wire patterns
        module filigree_wire(start, end, diameter=0.8) {
            translate(start) {
                rotate([0, 90, atan2(end[1]-start[1], end[0]-start[0])]) {
                    cylinder(h=norm(end-start), d=diameter, center=true);
                }
            }
        }

        // Decorative wire loops
        filigree_wire([-6, 10, 1], [-12, 15, 1]);
        filigree_wire([6, 10, 1], [12, 15, 1]);
        filigree_wire([-8, -5, 1], [-14, -10, 1]);
        filigree_wire([8, -5, 1], [14, -10, 1]);

        // Small spheres as pearl decorations
        for(i = [1:8]) {
            angle = i * 45;
            translate([cos(angle) * 3, sin(angle) * 3 + 20, 1.5]) {
                sphere(d=1.2);
            }
        }

        // Greek cross in center
        translate([0, 0, 2]) {
            cube([12, 1, 0.5], center=true);
            cube([1, 12, 0.5], center=true);
        }
    }

    // Hanging loop
    color("gold") translate([0, 42, 0]) {
        rotate([90, 0, 0]) {
            difference() {
                cylinder(h=2, d1=6, d2=6);
                cylinder(h=3, d1=3, d2=3);
            }
        }
    }

    // Beaded border decoration
    color("gold") for(t = [0:10:360]) {
        rotate([0, 0, t]) {
            translate([16, 0, 1]) sphere(d=1.5);
        }
    }
}

