module cross_base() {
    // Main cross body with slight taper
    color("gold") difference() {
        hull() {
            translate([0, 0, 0]) cube([cross_width, cross_width*0.6, thickness], center=true);
            translate([0, cross_height*0.3, 0]) cube([cross_width*0.8, cross_height*0.4, thickness], center=true);
        }

        // Central gem setting
        translate([0, cross_height*0.3, thickness/2]) 
            cylinder(h=thickness+1, r=cross_width*0.15, center=true);

        // Four smaller gem settings
        for(y=[-cross_height*0.2, cross_height*0.4]) {
            for(x=[-cross_width*0.3, cross_width*0.3]) {
                if(!(abs(x) < cross_width*0.2 && abs(y - cross_height*0.3) < cross_height*0.1)) {
                    translate([x, y, thickness/2]) 
                        cylinder(h=thickness+1, r=cross_width*0.08, center=true);
                }
            }
        }
    }
}

module decorative_inscription() {
    // Raised Greek inscription around edges
    color("darkgoldenrod") for(i=[0:3]) {
        rotate([0, 0, i*90]) 
            translate([0, cross_height*0.5, thickness/2]) 
                cylinder(h=thickness*0.3, r=cross_width*0.45, center=true);
    }
}

module suspension_loop() {
    // Cast loop for hanging
    color("gold") rotate([90, 0, 0]) 
        translate([0, cross_height*0.52, 0]) 
            difference() {
                cylinder(h=cross_width*0.15, r=cross_width*0.08, center=true);
                cylinder(h=cross_width*0.2, r=cross_width*0.04, center=true);
            }
}

module engraved_patterns() {
    // Engraved cross hatching pattern
    color("gold") for(i=[-2:2]) {
        // Vertical lines
        translate([i*cross_width*0.1, cross_height*0.3, 0]) 
            cube([cross_width*0.02, cross_height*0.5, thickness*0.2], center=true);
        // Horizontal lines
        translate([0, cross_height*0.3 + i*cross_height*0.1, 0]) 
            cube([cross_width*0.8, cross_width*0.02, thickness*0.2], center=true);
    }
}

module central_christogram() {
    // IC XC Christogram in center
    color("gold") translate([0, cross_height*0.3, thickness*0.4]) {
        // I
        cube([cross_width*0.05, cross_height*0.15, thickness*0.3], center=true);
        // C
        translate([-cross_width*0.15, 0, 0]) 
            difference() {
                cylinder(h=thickness*0.3, r=cross_width*0.1, center=true);
                translate([cross_width*0.05, 0, 0]) 
                    cube([cross_width*0.15, cross_width*0.2, thickness*0.4], center=true);
            }
        // X
        rotate([0, 0, 45]) cube([cross_width*0.05, cross_height*0.2, thickness*0.3], center=true);
        rotate([0, 0, -45]) cube([cross_width*0.05, cross_height*0.2, thickness*0.3], center=true);
        // C
        translate([cross_width*0.15, 0, 0]) mirror([1, 0, 0]) 
            difference() {
                cylinder(h=thickness*0.3, r=cross_width*0.1, center=true);
                translate([cross_width*0.05, 0, 0]) 
                    cube([cross_width*0.15, cross_width*0.2, thickness*0.4], center=true);
            }
    }
}

// Assembly
union() {
    cross_base();
    decorative_inscription();
    suspension_loop();
    engraved_patterns();
    central_christogram();
}

