    translate([-2.5, 19, 4]) cube([1.2, 8, 2.1]);
    // C
    translate([2.5, 19, 4]) cube([1.2, 8, 2.1]);
    translate([3.8, 21, 4]) cube([2.5, 1.2, 2.1]);
    translate([3.8, 24, 4]) cube([2.5, 1.2, 2.1]);
    // X
    translate([-2.5, 14, 4]) cube([1.2, 8, 2.1]);
    rotate([0, 0, 45]) translate([-2.5, 14, 4]) cube([1.2, 8, 2.1]);
    rotate([0, 0, -45]) translate([2.5, 14, 4]) cube([1.2, 8, 2.1]);
    // C
    translate([2.5, 9, 4]) cube([1.2, 8, 2.1]);
    translate([3.8, 11, 4]) cube([2.5, 1.2, 2.1]);
    translate([3.8, 14, 4]) cube([2.5, 1.2, 2.1]);
    // N
    translate([-2.5, 4, 4]) cube([1.2, 8, 2.1]);
    translate([2.5, 4, 4]) cube([1.2, 8, 2.1]);
    rotate([0, 0, 30]) translate([-2.5, 4, 4]) cube([1.2, 8, 2.1]);
    // I
    translate([-2.5, -1, 4]) cube([1.2, 8, 2.1]);
    // K
    translate([2.5, -1, 4]) cube([1.2, 4, 2.1]);
    rotate([0, 0, 45]) translate([2.5, -1, 4]) cube([1.2, 4, 2.1]);
    rotate([0, 0, -45]) translate([2.5, 3, 4]) cube([1.2, 4, 2.1]);
    // A
    translate([0, -6, 4]) {
        rotate([0, 0, 30]) translate([-2.5, 0, 0]) cube([1.2, 8, 2.1]);
        rotate([0, 0, -30]) translate([2.5, 0, 0]) cube([1.2, 8, 2.1]);
        translate([0, 3, 0]) cube([5, 1.2, 2.1]);
    }

    // Niello dots around perimeter
    module dot_ring(d) {
        for (a = [0:30:330]) {
            translate([sin(a)*d, cos(a)*d, 4]) cylinder(h=2.1, d=1.5, $fn=8);
        }
    }
    // Top arm
    dot_ring(17);
    // Bottom arm
    dot_ring(17);
    // Left arm
    dot_ring(17);
    // Right arm
    dot_ring(17);
}

// Suspension loop, printable without support
