// Cloisonn Enamel Bowl - Tang Dynasty Style (8th Century)
// Dimensions: 80mm diameter x 35mm height

// Parameters
bowl_radius = 40;
bowl_height = 35;
wall_thickness = 2;
base_thickness = 3;
cloison_thickness = 0.8;
segment_count = 8;

// Base bowl shell
module bowl_shell() {
    difference() {
        // Outer bowl
        cylinder(h=bowl_height, r=bowl_radius, $fn=100);

        // Hollow interior
        translate([0, 0, base_thickness])
            cylinder(h=bowl_height, r=bowl_radius-wall_thickness, $fn=100);

        // Flatten bottom slightly
        translate([0, 0, -1])
            cylinder(h=2, r=bowl_radius-5, $fn=100);
    }
}

// Cloisonn wire dividers
module cloison_wires() {
    // Vertical dividers
    for(i=[0:segment_count-1]) {
        rotate([0, 0, i * 360/segment_count])
            translate([bowl_radius-wall_thickness-0.5, 0, base_thickness])
                cube([cloison_thickness, 1, bowl_height-base_thickness], center=true);
    }

    // Horizontal rings
    for(h=[0.3, 0.6, 0.9]) {
        translate([0, 0, h * bowl_height])
            difference() {
                cylinder(h=cloison_thickness, r1=bowl_radius-wall_thickness, 
                        r2=bowl_radius-wall_thickness, $fn=100);
                cylinder(h=cloison_thickness+0.2, r1=bowl_radius-wall_thickness-5, 
                        r2=bowl_radius-wall_thickness-5, $fn=100);
            }
    }
}

// Enamel cells with colors
module enamel_cells() {
    colors = ["red", "blue", "green", "yellow", "cyan", "magenta", "orange", "purple"];

    for(i=[0:segment_count-1]) {
        angle = i * 360/segment_count;
        color(colors[i % len(colors)])
            rotate([0, 0, angle + 180/segment_count])
                translate([bowl_radius-wall_thickness-2.5, 0, bowl_height/2])
                    cube([4, 2, bowl_height-5], center=true);
    }
}

// Decorative rim pattern
module rim_pattern() {
    translate([0, 0, bowl_height-2])
        for(i=[0:20]) {
            rotate([0, 0, i * 18])
                translate([bowl_radius-2, 0, 0])
                    cube([1.5, 0.5, 2], center=true);
        }
}

// Lotus petal base decoration
module lotus_base() {
    petals = 12;
    for(i=[0:petals-1]) {
        rotate([0, 0, i * 360/petals])
            translate([bowl_radius-8, 0, 0])
                rotate([90, 0, 90])
                    scale([0.3, 1, 0.8])
                        cylinder(h=8, r1=3, r2=1, $fn=3);
    }
}

// Central medallion
module central_medallion() {
    translate([0, 0, base_thickness+0.5])
        difference() {
            cylinder(h=1, r=8, $fn=50);
            for(i=[0:5]) {
                rotate([0, 0, i * 72])
                    translate([3, 0, -0.1])
                        cube([6, 1, 1.2], center=true);
            }
        }
}

// Assembly
color("gold") {
    union() {
        bowl_shell();
        cloison_wires();
    }
}

enamel_cells();
color("gold") rim_pattern();
color("gold") lotus_base();
color("darkblue") central_medallion();

