// Guilloche Pendant from Ancient Greece - 5th Century BCE
// Realistic medium complexity model with proper 3D printing considerations

$fn = 64;

// Overall dimensions
outer_diameter = 60;
inner_diameter = 25;
thickness = 4;
loop_thickness = 3;

// Main pendant body
module pendant_body() {
    difference() {
        // Outer disk
        cylinder(h = thickness, d = outer_diameter, center = true);

        // Inner hole
        cylinder(h = thickness + 1, d = inner_diameter, center = true);
    }
}

// Guilloche pattern element - wave motif
module guilloche_wave(radius, height, angle) {
    rotate([0, 0, angle])
    translate([radius, 0, 0])
    rotate_extrude(angle = 30, $fn = 32)
    translate([2, 0, 0])
    circle(d = 1.5);
}

// Decorative border pattern
module border_pattern() {
    for(i = [0:15:360]) {
        rotate([0, 0, i])
        translate([(outer_diameter - 5) / 2, 0, thickness / 2])
        color("gold")
        sphere(d = 2.5);
    }
}

// Central rosette pattern
module central_rosette() {
    color("gold")
    for(i = [0:30:360]) {
        rotate([0, 0, i])
        translate([(inner_diameter + 10) / 2, 0, 0])
        cylinder(h = thickness, d = 4, center = true);
    }
}

// Greek key meander pattern
module meander_pattern() {
    color("gold")
    for(i = [0:45:360]) {
        rotate([0, 0, i])
        translate([0, 0, thickness / 2])
        linear_extrude(height = 1)
        polygon(points = [
            [18, -1], [22, -1], [22, 1], [18, 1], [18, 3], [20, 3], [20, 5], [18, 5],
            [16, 5], [16, 3], [18, 3], [18, 1], [16, 1], [16, -1], [18, -1]
        ]);
    }
}

// Suspension loop for wearing
module suspension_loop() {
    color("gold")
    translate([0, outer_diameter / 2 + 1, 0])
    rotate([90, 0, 0])
    difference() {
        torus(outer_diameter = 8, inner_diameter = 4, thickness = loop_thickness);
        cube([20, 20, 20], center = true);
    }
}

// Main torus module for suspension loop
module torus(outer_diameter, inner_diameter, thickness) {
    rotate_extrude($fn = 32)
    translate([outer_diameter / 2, 0, 0])
    circle(d = inner_diameter);
}

// Engraved inscription band
module inscription_band() {
    color("gold")
    difference() {
        cylinder(h = 1.5, d = outer_diameter - 10, center = true);

        // Greek letters (simplified)
        for(i = [0:30:330]) {
            rotate([0, 0, i])
            translate([(outer_diameter - 20) / 2, 0, 0])
            cube([1.5, 0.5, 2]);
        }
    }
}

// Main assembly
color("goldenrod")
union() {
    // Main pendant
    pendant_body();

    // Add layers for depth
    translate([0, 0, thickness / 2 + 0.75])
    color("gold")
    cylinder(h = 1.5, d = outer_diameter - 2, center = true);

    // Guilloche patterns on front
    for(angle = [0:15:345]) {
        color("gold")
        guilloche_wave((outer_diameter + inner_diameter) / 4, thickness, angle);
    }

    // Border decoration
    border_pattern();

    // Central rosette
    central_rosette();

    // Meander pattern
    meander_pattern();

    // Inscription
    translate([0, 0, -thickness / 2 - 0.75])
    inscription_band();

    // Suspension loop
    suspension_loop();
}

