        sphere(d=bead_diameter, $fn=12);
    }
}

module cross_symbol() {
    // Central cross with flared ends
    union() {
        // Vertical bar
        translate([0, 0, -symbol_depth/2])
        cube([2.5, 12, symbol_depth], center=true);

        // Horizontal bar
        translate([0, 0, -symbol_depth/2])
        cube([12, 2.5, symbol_depth], center=true);

