        translate([6, 0, -symbol_depth/2])
        rotate([0, 0, 90])
        cylinder(h=symbol_depth, r1=3, r2=2, $fn=20);

        translate([-6, 0, -symbol_depth/2])
        rotate([0, 0, 90])
        cylinder(h=symbol_depth, r1=3, r2=2, $fn=20);
